import React, { useState } from "react";
import { useProduct } from "../context/ProductContextProvider";
import { Box, TextField, Button } from "@mui/material";

const AddCategory = () => {
  const { createSport } = useProduct();
  const [sport, setSport] = useState({
    name: "",
  });

  const handleChange = (e) => {
    setSport({ ...sport, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createSport(sport);
    setSport({ ...sport, name: "" });
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        width: "23%",
        height: "auto",
        padding: 2,
        border: "1px solid black",
        borderRadius: 1,
        margin: "100px auto", // Центрируем по горизонтали
        backgroundColor: "#b5ffe1",
        color: "#01263a",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        position: "relative",
        top: "420px",
        right: "70px",

        "@media (max-width: 1200px)": {
          width: "30%", // На экранах до 1200px будет ширина 30%
          margin: "50px auto", // Центрируем с меньшими отступами
        },
        "@media (max-width: 992px)": {
          width: "40%", // На экранах до 992px будет ширина 40%
          margin: "40px auto",
        },
        "@media (max-width: 768px)": {
          width: "50%", // На экранах до 768px будет ширина 50%
          margin: "30px auto",
        },
        "@media (max-width: 480px)": {
          width: "50%", // На экранах до 480px будет ширина 70%
          margin: "20px auto",
        },
        "@media (max-width: 320px)": {
          width: "50%", // На экранах до 320px будет ширина 85%
          margin: "10px auto",
        },
      }}
    >
      <TextField
        type="text"
        name="name"
        value={sport.name}
        onChange={handleChange}
        label="Sport Name"
        variant="outlined"
        fullWidth
        sx={{ marginBottom: "16px", backgroundColor: "#ADE8FE" }}
      />
      <Button type="submit" variant="contained" color="primary">
        Add Category
      </Button>
    </Box>
  );
};

export default AddCategory;
