import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useAuth } from "../../components/context/AuthContextProvider";
import { ADMIN_EMAIL } from "../../helpers/const";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
import "./Navbar.css";

const Navbar = () => {
  const { user, handleLogOut } = useAuth();
  const [isAdmin, setIsAdmin] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    if (user) {
      setIsAdmin(user.email === ADMIN_EMAIL);
    }
  }, [user]);

  const pages = [
    { id: 1, title: "Атлеты", link: "/athletes" },
    { id: 2, title: "Виды спорта", link: "/sports" },
    { id: 3, title: "Медальный зачет", link: "/medal-table" },
  ];

  if (isAdmin) {
    pages.push({ id: 4, title: "Админ", link: "/admin" });
  }

  return (
    <div style={styles.root}>
      <AppBar position="static" style={styles.appBar}>
        <Toolbar style={styles.toolbar}>
          <div style={styles.leftItems}>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Flag_of_Kyrgyzstan_%283-2%29.svg/1599px-Flag_of_Kyrgyzstan_%283-2%29.svg.png?20240311163127"
              style={{ ...styles.icon, width: "auto", height: 40 }}
              alt="Flag"
            />
            <Link to={"/"} style={{ textDecoration: "none" }}>
              <Typography
                variant="h6"
                style={styles.menuItem}
                onMouseEnter={(e) => (e.currentTarget.style.color = "red")}
                onMouseLeave={(e) => (e.currentTarget.style.color = "#ffffff")}
              >
                Спортсмены КР
              </Typography>
            </Link>
          </div>

          {/* Центр навигации теперь всегда отображается */}
          <div style={{ ...styles.centerItems, flex: 1 }}>
            {pages.map((page, index) => (
              <IconButton
                key={page.id}
                color="inherit"
                component={Link}
                to={page.link}
                style={{
                  ...styles.menuButton,
                  marginLeft: index > 0 ? 10 : 0,
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.firstChild.style.color = "red")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.firstChild.style.color = "#ffffff")
                }
              >
                <Typography variant="body1" style={styles.menuItem}>
                  {page.title}
                </Typography>
              </IconButton>
            ))}
          </div>

          <div
            style={{
              ...styles.rightItems,
              marginLeft: "auto",
              display: "flex",
              alignItems: "center",
            }}
          >
            <Typography
              variant="body1"
              style={{ color: "#ffffff", marginRight: "15px" }}
            >
              {user
                ? `Добро пожаловать, ${user.displayName || user.email}`
                : "Добро пожаловать, гость"}
            </Typography>
            <IconButton
              edge="end"
              color="inherit"
              aria-label="menu"
              onClick={() => setDrawerOpen(true)}
              style={styles.menuButton}
            >
              <MenuIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        style={styles.drawer}
        transitionDuration={500}
      >
        <div style={styles.drawerHeader}>
          <IconButton
            onClick={() => setDrawerOpen(false)}
            style={styles.closeButton}
            onMouseEnter={(e) => (e.currentTarget.style.color = "red")}
            onMouseLeave={(e) => (e.currentTarget.style.color = "#000")}
          >
            <CloseIcon />
          </IconButton>
        </div>
        <List style={styles.drawerList}>
          {/* Теперь сообщение о пользователе всегда отображается */}
          <ListItem button>
            <Typography style={{ color: "#000000" }}>
              {user
                ? isAdmin
                  ? `Добро пожаловать, ${user.email}`
                  : `Добро пожаловать, ${user.displayName || user.email}`
                : "Добро пожаловать, гость"}
            </Typography>
          </ListItem>

          {!user && (
            <ListItem button component={Link} to="/register">
              <ListItemText primary="Регистрация" />
            </ListItem>
          )}
          {user && (
            <ListItem button onClick={handleLogOut}>
              <ListItemText primary="Выйти" />
            </ListItem>
          )}
          {pages.map((page) => (
            <ListItem button key={page.id} component={Link} to={page.link}>
              <ListItemText primary={page.title} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </div>
  );
};

const styles = {
  root: {
    flexGrow: 1,
    width: "100%",
    height: "auto",
    overflow: "hidden",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    width: "auto",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
    height: "40px",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: "40px",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
    height: "40px",
  },
  menuButton: {
    marginRight: 10,
  },
  menuItem: {
    color: "#ffffff",
    transition: "color 0.3s",
    fontSize: "1rem",
    fontWeight: "bold",
  },
  icon: {
    width: "auto",
    height: 40,
    marginRight: 10,
  },
  drawerList: {
    width: 250,
    transition: "background-color 0.3s ease",
  },
  drawerHeader: {
    display: "flex",
    justifyContent: "flex-end",
    padding: "10px",
  },
  closeButton: {
    color: "#000",
  },
  drawer: {
    backgroundColor: "rgba(0, 0, 0, 0.9)",
    transition: "background-color 0.3s ease",
    paddingTop: "10px",
  },
};

export default Navbar;
