import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const quizQuestions = [
  {
    question: "Столица Кыргызстана?",
    options: ["Бишкек", "Ош", "Каракол", "Талас"],
    answer: "Бишкек",
  },
  {
    question: "Национальная валюта Кыргызстана?",
    options: ["Тенге", "Рубль", "Сом", "Драм"],
    answer: "Сом",
  },
  {
    question: "Самая высокая гора Кыргызстана?",
    options: ["Хан-Тенгри", "Победа", "Тянь-Шань", "Эверест"],
    answer: "Победа",
  },
  {
    question: "Какой вид спорта наиболее популярен в Кыргызстане?",
    options: ["Футбол", "Кок-бору", "Бокс", "Шахматы"],
    answer: "Кок-бору",
  },
  {
    question: "В каком году Кыргызстан получил независимость?",
    options: ["1990", "1991", "1992", "1993"],
    answer: "1991",
  },
  {
    question: "Главное озеро Кыргызстана?",
    options: ["Балхаш", "Иссык-Куль", "Каспий", "Арал"],
    answer: "Иссык-Куль",
  },
  {
    question: "Национальное блюдо Кыргызстана?",
    options: ["Борщ", "Плов", "Бешбармак", "Мампар"],
    answer: "Бешбармак",
  },
  {
    question: "Как называется традиционная юрта?",
    options: ["Гер", "Боз үй", "Чум", "Тепе"],
    answer: "Боз үй",
  },
  {
    question: "Какая крупнейшая река в Кыргызстане?",
    options: ["Чу", "Нарын", "Талас", "Сырдарья"],
    answer: "Нарын",
  },
  {
    question: "Как называется национальный головной убор кыргызов?",
    options: ["Калпак", "Папаха", "Феска", "Кепка"],
    answer: "Калпак",
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSelectAnswer = (question, answer) => {
    setSelectedAnswers({ ...selectedAnswers, [question]: answer });
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < quizQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handleSubmitQuiz = () => {
    setShowResults(true);
  };

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене.
          </Typography>
        </Box>
      </Box>

      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
          }}
        />
      </Box>

      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "center",
          gap: "20px",
          padding: "40px",
        }}
      >
        <Card
          sx={{
            width: "300px",
            padding: "20px",
            transition: "0.3s",
            "&:hover": { transform: "scale(1.05)" },
          }}
        >
          <CardContent>
            <Typography variant="h6" sx={{ marginBottom: "10px" }}>
              {quizQuestions[currentQuestionIndex].question}
            </Typography>
            <RadioGroup
              onChange={(e) =>
                handleSelectAnswer(
                  quizQuestions[currentQuestionIndex].question,
                  e.target.value
                )
              }
            >
              {quizQuestions[currentQuestionIndex].options.map((option, i) => (
                <FormControlLabel
                  key={i}
                  value={option}
                  control={<Radio />}
                  label={option}
                />
              ))}
            </RadioGroup>
          </CardContent>
        </Card>
      </Box>

      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          gap: "20px",
          marginTop: "20px",
        }}
      >
        <Button
          variant="contained"
          onClick={handleNextQuestion}
          sx={{
            backgroundColor: "#ff4d6d",
            "&:hover": { backgroundColor: "#d43f5e" },
          }}
        >
          Следующий вопрос
        </Button>
        {currentQuestionIndex === quizQuestions.length - 1 && (
          <Button
            variant="contained"
            onClick={handleSubmitQuiz}
            sx={{
              backgroundColor: "#ff4d6d",
              "&:hover": { backgroundColor: "#d43f5e" },
            }}
          >
            Проверить ответы
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

{
  /* <div className="main">
<div className="overlay"></div>
<video className="background-video" src={videoBg} autoPlay loop muted />
<div className="content">
  <h4>
    Recipes, books, establishments - everything for chefs and cooking
    lovers. Let's discover new tastes of the world together!
  </h4>
</div>
</div> */
}
// анимации, красивый дизайн и взаимодействие для привлечения внимания пользователя, в этом коде исправь викторину сделай намного лучше и
