import React from "react";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  return (
    <div>
      {/* Видео фон */}
      <div
        style={{
          position: "relative",
          width: "100%",
          height: "100vh", // Видео занимает всю высоту экрана
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
          }}
        >
          Ваш браузер не поддерживает видео.
        </video>

        {/* Контент поверх видео */}
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            textAlign: "center",
            color: "white",
          }}
        >
          <div
            style={{
              maxWidth: "800px",
              padding: "20px",
            }}
          >
            <h1
              style={{
                fontSize: "48px",
                marginBottom: "10px",
                fontWeight: "bold",
                fontFamily: "Georgia, serif",
              }}
            >
              Спортсмены Кыргызстана
            </h1>
            <p
              style={{
                fontSize: "20px",
                lineHeight: "1.4",
                fontWeight: "bold",
              }}
            >
              Кыргызстан — страна в Центральной Азии, известная своей живописной
              природой, горами Тянь-Шань, кристально чистыми озерами и богатой
              культурой. Здесь проживает около 6,5 миллионов человек, а столица
              страны — Бишкек. В Кыргызстане особенно ценятся традиции,
              гостеприимство и любовь к национальным видам спорта, таким как
              кок-бору и борьба. Сочетая древние традиции и современные
              достижения, Кыргызстан становится популярным местом для туризма и
              активно развивает спорт на мировой арене, в том числе через
              участие своих спортсменов в международных соревнованиях.
            </p>
          </div>
        </div>
      </div>

      {/* Изображение, которое отображается ниже видео */}
      <div
        style={{
          width: "100%",
          height: "auto",
          backgroundImage: `url(
            "https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          )`,
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundSize: "cover",
          minHeight: "300px", // Минимальная высота изображения
        }}
      ></div>
    </div>
  );
};

export default MainPage;

