import React, { useState, useEffect } from "react";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  const [isVideoEnded, setIsVideoEnded] = useState(false);

  useEffect(() => {
    // After the video ends, show the image
    const videoElement = document.getElementById("background-video");
    videoElement.onended = () => {
      setIsVideoEnded(true);
    };
  }, []);

  return (
    <div
      style={{
        position: "relative",
        minHeight: "103vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundImage: isVideoEnded
          ? `url("https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg")`
          : "none",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        backgroundSize: "cover",
        fontFamily: "Snell Roundhand, cursive",
      }}
      className="home-main-page"
    >
      {/* Video background */}
      <video
        id="background-video"
        className="background-video"
        src={videoBg}
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "120%",
          objectFit: "cover", // Ensures the video fills the screen without distortion
          transition: "opacity 1s ease-out",
          objectPosition: "top center", // Фокусировка на центре видео (где чаще всего лица)
        }}
      >
        Ваш браузер не поддерживает видео.
      </video>

      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // semi-transparent overlay
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
        className="content"
      >
        <div
          style={{
            maxWidth: "800px",
            padding: "20px",
            textAlign: "center",
            color: "white",
          }}
        >
          <h1
            style={{
              fontSize: "48px",
              marginBottom: "10px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
            }}
          >
            Спортсмены Кыргызстана
          </h1>
          <p
            style={{ fontSize: "20px", lineHeight: "1.4", fontWeight: "bold" }}
          >
            Кыргызстан — страна в Центральной Азии, известная своей живописной
            природой, горами Тянь-Шань, кристально чистыми озерами и богатой
            культурой. Здесь проживает около 6,5 миллионов человек, а столица
            страны — Бишкек. В Кыргызстане особенно ценятся традиции,
            гостеприимство и любовь к национальным видам спорта, таким как
            кок-бору и борьба. Сочетая древние традиции и современные
            достижения, Кыргызстан становится популярным местом для туризма и
            активно развивает спорт на мировой арене, в том числе через участие
            своих спортсменов в международных соревнованиях.
          </p>
        </div>
      </div>
    </div>
  );
};

export default MainPage;

