import React, { createContext, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { firebaseConfig } from "./firebaseConfig"; // Ваши конфигурации Firebase
import { initializeApp } from "/firebase/app";
import { getAuth, signInWithEmailAndPassword } from "/firebase/auth";

// Инициализация Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthContextProvider = ({ children }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState([]);
  const [passwordError, setPasswordError] = useState([]);
  const navigate = useNavigate();

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError([]);
    setPasswordError([]);
  };

  const handleLogin = () => {
    // Проверка на пустые поля
    if (!email.trim() || !password.trim()) {
      return;
    }

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        const user = userCredential.user;

        console.log("Logged in user email:", user.email);

        // Проверка, администратор ли это
        if (user.email.toLowerCase().trim() === "admin@example.com") {
          console.log("Admin user detected");
          navigate("/admin"); // Переход на страницу администратора
        } else {
          console.log("Regular user detected");
          navigate("/"); // Переход на главную страницу для обычных пользователей
        }
      })
      .catch((error) => {
        console.log("Login error:", error);

        // Обработка ошибок
        switch (error.code) {
          case "auth/user-disabled":
          case "auth/invalid-email":
          case "auth/user-not-found":
            setEmailError(Object.values(error.code));
            break;
          case "auth/wrong-password":
            setPasswordError(Object.values(error.code));
            break;
          default:
            break;
        }
      });

    clearInputs();
    clearErrors();
  };

  return (
    <AuthContext.Provider
      value={{
        email,
        setEmail,
        password,
        setPassword,
        emailError,
        passwordError,
        handleLogin,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
