import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Flag_of_the_KR from "../homePage/assets/Flag_of_the_KR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);
  const [currentQuestion, setCurrentQuestion] = useState(0);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestion].correct;
    if (index === correctOption) {
      setQuizFeedback("Правильный ответ!");
      setQuizResults(quizResults + 1);
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова.");
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption(null);
      setQuizFeedback("");
    } else {
      setShowQuiz(true); // Показываем результаты после последнего вопроса
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Викторина с фоновым видео */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        {/* Видео фон */}
        <video
          src={Flag_of_the_KR} // Используем твой видеофайл
          autoPlay
          loop
          muted
          playsInline
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            zIndex: -1,
            filter: "brightness(50%)", // Затемняем видео для читаемости текста
          }}
        />

        {/* Затемняющий оверлей */}
        <Box
          sx={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.5)", // Полупрозрачный черный фон
            zIndex: -1,
          }}
        ></Box>

        {/* Контент викторины */}
        <Typography
          variant="h2"
          sx={{
            marginBottom: "20px",
            fontWeight: "bold",
            color: "#ffcc00",
            fontSize: "28px",
            letterSpacing: "2px",
            textTransform: "uppercase",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>

        {showQuiz ? (
          <Typography
            sx={{
              marginTop: "20px",
              fontSize: "24px",
              fontWeight: "bold",
              color: quizResults === questions.length ? "green" : "red",
            }}
          >
            Результат: {quizResults} из {questions.length} правильных ответов
          </Typography>
        ) : (
          <Box>
            <Card
              sx={{
                maxWidth: 400,
                margin: "20px auto",
                padding: "20px",
                bgcolor:
                  "ackground: radial-gradient(circle, rgba(255, 255, 255, 0.3), rgba(0, 0, 0, 0.1))",
              }}
            >
              <CardContent>
                <Typography
                  variant="h5"
                  sx={{ fontSize: "22px", fontWeight: "600" }}
                >
                  {questions[currentQuestion].question}
                </Typography>
                <Box
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    gap: "15px",
                    marginTop: "20px",
                  }}
                >
                  {questions[currentQuestion].options.map((option, idx) => (
                    <Button
                      key={idx}
                      variant="contained"
                      color={selectedOption === idx ? "success" : "primary"}
                      onClick={() => handleOptionClick(idx)}
                      sx={{
                        background:
                          selectedOption === idx ? "#ff4d6d" : "#007bff",
                        transition: "all 0.3s ease",
                        ":hover": {
                          transform: "scale(1.1)",
                          background: "#ff4d6d",
                        },
                        padding: "15px 30px",
                        borderRadius: "10px",
                      }}
                    >
                      {option}
                    </Button>
                  ))}
                </Box>
                <Typography
                  sx={{
                    marginTop: "10px",
                    fontWeight: "bold",
                    color:
                      quizFeedback === "Правильный ответ!" ? "green" : "red",
                  }}
                >
                  {quizFeedback}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleNextQuestion}
                  sx={{
                    marginTop: "30px",
                    padding: "12px 20px",
                    fontSize: "18px",
                    borderRadius: "10px",
                    transition: "transform 0.3s ease",
                    ":hover": { transform: "scale(1.05)" },
                  }}
                >
                  {currentQuestion < questions.length - 1
                    ? "Следующий вопрос"
                    : "Завершить викторину"}
                </Button>
              </CardActions>
            </Card>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

