import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Confetti from "react-confetti";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isQuizFinished, setIsQuizFinished] = useState(false);
  const [score, setScore] = useState({ correct: 0, incorrect: 0 });
  const [questionIndex, setQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана завоевал бронзовую медаль в борьбе на Олимпийских играх 2024 года?",
      options: [
        "Акжол Махмудов",
        "Айсулуу Тыныбекова",
        "Мээрим Джуманазарова",
        "Жоломан Шаршенбеков",
      ],
      answer: "Акжол Махмудов",
    },
    {
      question:
        "Кто из спортсменок Кыргызстана стала первой, кто завоевал две медали на Олимпийских играх?",
      options: [
        "Мээрим Джуманазарова",
        "Айсулуу Тыныбекова",
        "Жоломан Шаршенбеков",
        "Мунарбек Сейитбек уулу",
      ],
      answer: "Мээрим Джуманазарова",
    },
    {
      question:
        "Какая медаль была завоевана Мунарбеком Сейитбек уулу на Олимпиаде в 2024 году?",
      options: ["Золотая", "Серебряная", "Бронзовая", "Нет медали"],
      answer: "Серебряная",
    },
    {
      question:
        "Кто из кыргызстанских спортсменов завоевал бронзовую медаль в греко-римской борьбе на Олимпиаде 2024?",
      options: [
        "Жоломан Шаршенбеков",
        "Акжол Махмудов",
        "Айсулуу Тыныбекова",
        "Узур Джузупбеков",
      ],
      answer: "Жоломан Шаршенбеков",
    },
    {
      question:
        "Какое место занял Кыргызстан в медальном зачете на Олимпийских играх 2024 года?",
      options: ["60-е", "68-е", "50-е", "72-е"],
      answer: "68-е",
    },
  ];

  const handleAnswerSelect = (option) => {
    setSelectedOption(option);
    if (option === questions[questionIndex].answer) {
      setIsCorrect(true);
      setScore({ ...score, correct: score.correct + 1 });
    } else {
      setIsCorrect(false);
      setScore({ ...score, incorrect: score.incorrect + 1 });
    }

    setTimeout(() => {
      if (questionIndex + 1 < questions.length) {
        setQuestionIndex(questionIndex + 1);
        setSelectedOption(null);
        setIsCorrect(null);
      } else {
        setIsQuizFinished(true);
        setShowConfetti(true);
      }
    }, 1000);
  };

  const handleRestartQuiz = () => {
    setIsQuizFinished(false);
    setQuestionIndex(0);
    setScore({ correct: 0, incorrect: 0 });
    setSelectedOption(null);
    setIsCorrect(null);
    setShowConfetti(false);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(60%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        {!isQuizFinished ? (
          <div className="quiz-container">
            <h3 className="question">{questions[questionIndex].question}</h3>
            <div className="options">
              {questions[questionIndex].options.map((option, index) => (
                <button
                  key={index}
                  className={`option-button ${
                    selectedOption === option
                      ? isCorrect
                        ? "correct"
                        : "incorrect"
                      : ""
                  }`}
                  onClick={() => handleAnswerSelect(option)}
                >
                  {option}
                </button>
              ))}
            </div>

            {isCorrect !== null && (
              <p className={`feedback ${isCorrect ? "correct" : "incorrect"}`}>
                {isCorrect ? "Правильно!" : "Неправильно, попробуйте снова."}
              </p>
            )}

            {showConfetti && <Confetti />}
          </div>
        ) : (
          <div className="quiz-results">
            <h3>Викторина завершена!</h3>
            <p>Правильных ответов: {score.correct}</p>
            <p>Неправильных ответов: {score.incorrect}</p>
            <button className="restart-button" onClick={handleRestartQuiz}>
              Начать заново
            </button>
          </div>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

