import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import natureKR from "../homePage/assets/natureKR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0); // Индекс текущего вопроса
  const [isAnswered, setIsAnswered] = useState(false); // Отслеживаем, был ли ответ
  const [isCorrect, setIsCorrect] = useState(false); // Правильность ответа
  const [showResult, setShowResult] = useState(false); // Показать результат викторины
  const questions = [
    {
      question: "Какой вид спорта является национальным в Кыргызстане?",
      options: ["Бокс", "Футбол", "Куреш", "Баскетбол"],
      answer: "Куреш",
    },
    {
      question: "В каком году Кыргызстан стал независимым?",
      options: ["1991", "1995", "1992", "1990"],
      answer: "1991",
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswer = (selectedAnswer) => {
    setIsAnswered(true);
    const correct = selectedAnswer === questions[questionIndex].answer;
    setIsCorrect(correct);

    if (correct) {
      // Включить анимацию или эффекты для правильного ответа (например, салют)
      setTimeout(() => setShowResult(true), 2000); // Показать результат через 2 секунды
    } else {
      // Включить эффект для неправильного ответа (например, неудачный звук)
      setTimeout(() => setShowResult(true), 2000); // Показать результат через 2 секунды
    }
  };

  const handleNextQuestion = () => {
    setIsAnswered(false);
    setIsCorrect(false);
    setShowResult(false);
    setQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Викторина о Кыргызстане
          </Typography>

          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
              opacity: isVisible ? 1 : 0,
              transform: isVisible ? "translateY(0)" : "translateY(30px)",
              transition: "all 0.8s ease-out",
            }}
          >
            {questions[questionIndex].question}
          </Typography>

          <Box sx={{ marginTop: "20px" }}>
            {questions[questionIndex].options.map((option, index) => (
              <Button
                key={index}
                variant="contained"
                onClick={() => handleAnswer(option)}
                sx={{
                  margin: "5px",
                  backgroundColor: "#ff4d6d",
                  "&:hover": { backgroundColor: "#ff3366" },
                }}
              >
                {option}
              </Button>
            ))}
          </Box>

          {isAnswered && (
            <Typography
              sx={{
                marginTop: "20px",
                color: isCorrect ? "green" : "red",
                fontWeight: "bold",
                fontSize: "20px",
              }}
            >
              {isCorrect ? "Правильный ответ!" : "Неправильный ответ!"}
            </Typography>
          )}

          {showResult && (
            <Box sx={{ marginTop: "30px" }}>
              <Typography variant="h5">
                Результат викторины:{" "}
                {isCorrect ? "Вы выиграли!" : "Попробуйте снова!"}
              </Typography>
              <Button
                variant="outlined"
                sx={{ marginTop: "20px" }}
                onClick={handleNextQuestion}
              >
                Перейти к следующему вопросу
              </Button>
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default MainPage;

