import React, { useState, useEffect } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [quizAnswer, setQuizAnswer] = useState(null);
  const [quizCompleted, setQuizCompleted] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Вопросы для викторины
  const quizQuestions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом по борьбе?",
      options: [
        "Руслан Тюлеев",
        "Медет Арсланбеков",
        "Данияр Ахмедов",
        "Бекболот Тулеев",
      ],
      correctAnswer: "Руслан Тюлеев",
    },
    {
      question: "Какая страна принимала Олимпийские игры 2020 года?",
      options: ["Япония", "Китай", "Россия", "Франция"],
      correctAnswer: "Япония",
    },
    // Добавьте другие вопросы по желанию
  ];

  const handleAnswerClick = (answer, correctAnswer) => {
    setQuizAnswer(answer);
    if (answer === correctAnswer) {
      setQuizCompleted(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина о олимпийских спортсменах */}
      <Box
        sx={{
          padding: "30px",
          backgroundColor: "#1f1f1f",
          marginTop: "40px",
          borderRadius: "8px",
          boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
        }}
      >
        <Typography
          variant="h4"
          sx={{
            color: "white",
            marginBottom: "20px",
            fontWeight: "bold",
            textAlign: "center",
          }}
        >
          Викторина о олимпийских спортсменах Кыргызстана
        </Typography>
        {quizQuestions.map((question, index) => (
          <Box
            key={index}
            sx={{
              marginBottom: "20px",
              backgroundColor: "#2b2b2b",
              padding: "20px",
              borderRadius: "8px",
            }}
          >
            <Typography variant="h6" sx={{ color: "white" }}>
              {question.question}
            </Typography>
            <Box sx={{ marginTop: "10px" }}>
              {question.options.map((option, optionIndex) => (
                <Button
                  key={optionIndex}
                  variant="contained"
                  color="primary"
                  sx={{
                    margin: "5px",
                    backgroundColor: quizAnswer === option ? "green" : "",
                  }}
                  onClick={() =>
                    handleAnswerClick(option, question.correctAnswer)
                  }
                >
                  {option}
                </Button>
              ))}
            </Box>
            {quizCompleted && quizAnswer === question.correctAnswer && (
              <Typography sx={{ color: "lime", marginTop: "10px" }}>
                Правильный ответ!
              </Typography>
            )}
            {quizCompleted && quizAnswer !== question.correctAnswer && (
              <Typography sx={{ color: "red", marginTop: "10px" }}>
                Неправильный ответ! Попробуйте снова.
              </Typography>
            )}
          </Box>
        ))}
      </Box>
    </Box>
  );
};

export default MainPage;

