import React, { useState } from "react";
import { useAuth } from "../components/context/AuthContextProvider";
import { useNavigate } from "react-router-dom";
import "../pages/Auth.css";

const Auth = () => {
  const {
    email,
    setEmail,
    password,
    setPassword,
    handleRegister,
    handleLogin,
    emailError,
    passwordError,
    setEmailError,
    setPasswordError,
  } = useAuth();

  const [isRegister, setIsRegister] = useState(true);
  const [name, setName] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setConfirmPasswordError("");
    setEmailError(""); // очищаем старую ошибку email
    setPasswordError(""); // очищаем старую ошибку password

    // Проверка совпадения паролей на этапе регистрации
    if (isRegister && password !== confirmPassword) {
      setConfirmPasswordError("Пароли не совпадают");
      return;
    }

    // Проверка на пустые поля
    if (email && password) {
      try {
        if (isRegister) {
          await handleRegister(); // Регистрация
        } else {
          await handleLogin(); // Вход
        }
      } catch (error) {
        console.log(error); // Логируем ошибку для диагностики
        // Обработка ошибок при регистрации и входе
        if (error.code === "auth/email-already-in-use") {
          setEmailError("Электронная почта уже используется");
        } else if (error.code === "auth/invalid-email") {
          setEmailError("Неверная электронная почта");
        } else if (error.code === "auth/weak-password") {
          setPasswordError("Пароль должен быть более 6 символов");
        } else if (error.code === "auth/user-not-found") {
          setEmailError("Пользователь с таким email не найден");
        } else if (error.code === "auth/wrong-password") {
          setPasswordError("Неверный пароль");
        } else {
          // Неизвестные ошибки
          setEmailError("Неизвестная ошибка входа");
          setPasswordError("Неизвестная ошибка входа");
        }
      }
    } else {
      if (!email) setEmailError("Введите электронную почту");
      if (!password) setPasswordError("Введите пароль");
    }
  };

  return (
    <div className="auth-container">
      <div className="left-section">
        <video autoPlay loop muted preload="auto">
          <source src="/video/Flag_of_the_KR.mp4" type="video/mp4" />
          Ваш браузер не поддерживает видео.
        </video>
      </div>

      <div className="right-section">
        <div className="auth-form">
          <h2 className="auth-title">{isRegister ? "Регистрация" : "Вход"}</h2>
          <form onSubmit={handleSubmit}>
            {isRegister && (
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Ваше имя"
                  required
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
            )}
            <div className="input-group">
              <input
                type="email"
                placeholder="Электронная почта"
                autoFocus
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <p className="error-msg">{emailError}</p>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="Пароль"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <p className="error-msg">{passwordError}</p>
            </div>
            {isRegister && (
              <div className="input-group">
                <input
                  type="password"
                  placeholder="Подтвердите пароль"
                  required
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <p className="error-msg">{confirmPasswordError}</p>
              </div>
            )}
            <div className="btn-container">
              <button type="submit">
                {isRegister ? "Регистрация" : "Вход"}
              </button>
              <p>
                {isRegister ? "Уже есть аккаунт?" : "Нет аккаунта?"}{" "}
                <span onClick={() => setIsRegister(!isRegister)}>
                  {isRegister ? "Вход" : "Регистрация"}
                </span>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Auth;
