import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import Confetti from "react-confetti"; // Подключаем библиотеку для салютов
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import natureKR from "../homePage/assets/natureKR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false); // Для отображения салютов
  const [isWrongAnswer, setIsWrongAnswer] = useState(false); // Для отслеживания неправильного ответа
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Индекс текущего вопроса
  const [quizCompleted, setQuizCompleted] = useState(false); // Статус завершения викторины

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const questions = [
    {
      question: "Какой самый высокий пик в Кыргызстане?",
      answers: ["Пик Ленина", "Пик Победы", "Пик Манас", "Пик Корона"],
      correctAnswer: "Пик Победы",
    },
    {
      question: "Какое национальное блюдо кыргызов?",
      answers: ["Бешбармак", "Лагман", "Манты", "Жашылык"],
      correctAnswer: "Бешбармак",
    },
    {
      question: "Какой напиток является традиционным для кыргызов?",
      answers: ["Кумыс", "Айран", "Шубат", "Чай"],
      correctAnswer: "Кумыс",
    },
    {
      question: "Когда Кыргызстан объявил свою независимость?",
      answers: ["1991 год", "1995 год", "1989 год", "2000 год"],
      correctAnswer: "1991 год",
    },
    {
      question: "Какая столица Кыргызстана?",
      answers: ["Бишкек", "Ош", "Нарын", "Талас"],
      correctAnswer: "Бишкек",
    },
    {
      question: "Какой язык является государственным в Кыргызстане?",
      answers: ["Русский", "Казахский", "Кыргызский", "Таджикский"],
      correctAnswer: "Кыргызский",
    },
    {
      question: "Какая валюта используется в Кыргызстане?",
      answers: ["Тенге", "Рубль", "Сом", "Доллар"],
      correctAnswer: "Сом",
    },
    {
      question:
        "Как называется национальный праздник Кыргызстана, посвящённый независимости?",
      answers: ["Нооруз", "День независимости", "Манас", "Ош"],
      correctAnswer: "День независимости",
    },
    {
      question:
        "Какая горная система занимает большую часть территории Кыргызстана?",
      answers: ["Горы Тянь-Шаня", "Кавказ", "Урал", "Альпы"],
      correctAnswer: "Горы Тянь-Шаня",
    },
    {
      question: "Какое животное является символом Кыргызстана?",
      answers: ["Овца", "Лошадь", "Козёл", "Як"],
      correctAnswer: "Лошадь",
    },
  ];

  const handleAnswerClick = (answer) => {
    if (quizCompleted) return; // Если викторина завершена, игнорировать ответы
    setSelectedAnswer(answer);
    const correct = answer === questions[currentQuestionIndex].correctAnswer;
    setIsCorrect(correct);
    setIsWrongAnswer(!correct);
    if (correct) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000); // Салют будет отображаться 5 секунд
    } else {
      setTimeout(() => setIsWrongAnswer(false), 1500); // Убираем анимацию через 1.5 секунды
    }
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedAnswer(null);
      setIsCorrect(null);
      setIsWrongAnswer(false);
    } else {
      setQuizCompleted(true);
    }
  };

  const { question, answers } = questions[currentQuestionIndex];

  const resetQuiz = () => {
    setQuizCompleted(false);
    setCurrentQuestionIndex(0);
    setSelectedAnswer(null);
    setIsCorrect(null);
    setIsWrongAnswer(false);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Викторина с фоновым видео */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover", // Измени objectFit
            filter: "none",
            zIndex: -1,
          }}
        />

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)", // Затемнённый полупрозрачный фон
            borderRadius: "10px", // Скругление углов
            padding: "20px",
            width: "100%",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.5)",
            transition: "transform 0.3s ease",
            transform: isWrongAnswer ? "translateX(-10px)" : "translateX(0)", // Анимация при неправильном ответе
          }}
        >
          <Typography
            variant="h5"
            sx={{
              fontWeight: "bold",
              fontSize: "24px",
              marginBottom: "20px",
              color: "orange",
            }}
          >
            Вопрос {currentQuestionIndex + 1}: {question}
          </Typography>

          {answers.map((answer, index) => (
            <Button
              key={index}
              variant="contained"
              color={
                isCorrect === true
                  ? "success"
                  : isCorrect === false
                  ? "error"
                  : "primary"
              }
              fullWidth
              sx={{
                marginBottom: "10px",
                backgroundColor:
                  selectedAnswer === answer ? "#ff4d6d" : "#ffa500",
                "&:hover": {
                  backgroundColor:
                    selectedAnswer === answer ? "#ff4d6d" : "#ff8c1a",
                },
              }}
              onClick={() => handleAnswerClick(answer)}
            >
              {answer}
            </Button>
          ))}
          {quizCompleted && (
            <Box sx={{ marginTop: "20px" }}>
              <Typography variant="h6" sx={{ color: "white" }}>
                Викторина завершена! Спасибо за участие.
              </Typography>
              <Button
                variant="contained"
                sx={{
                  backgroundColor: "#ff4d6d",
                  marginTop: "10px",
                  "&:hover": {
                    backgroundColor: "#ff3b56",
                  },
                }}
                onClick={resetQuiz}
              >
                Пройти снова
              </Button>
            </Box>
          )}
          {!quizCompleted && (
            <Button
              variant="contained"
              sx={{
                marginTop: "20px",
                backgroundColor: "#ffa500",
                "&:hover": {
                  backgroundColor: "#ff8c1a",
                },
              }}
              onClick={nextQuestion}
            >
              Далее
            </Button>
          )}
        </Card>
      </Box>

      {/* Показать салюты */}
      {showConfetti && <Confetti />}
    </Box>
  );
};

export default MainPage;

