import React, { useState } from "react";
import Modal from "react-modal";
import "./SportsPage.css";
const sports = [
  {
    name: "Баскетбол",
    imageUrl:
      "https://sport.kg/uploads/posts/2017-10/1508912742_basket-ikonka.jpg",
