import React, { useState, useEffect } from "react";
import { Button, Box, Typography, TextField, Fade } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [isQuizCompleted, setIsQuizCompleted] = useState(false);
  const [score, setScore] = useState(0);

  const questions = [
    {
      question: "Кто стал первым олимпийским чемпионом Кыргызстана?",
      options: [
        "Санжар Кадыров",
        "Азат Токтогулов",
        "Данияр Кебеков",
        "Айжан Шакирова",
      ],
      correctAnswer: "Санжар Кадыров",
    },
    {
      question:
        "В каком виде спорта Кыргызстан завоевал свою первую олимпийскую медаль?",
      options: ["Бокс", "Тхэквондо", "Легкая атлетика", "Дзюдо"],
      correctAnswer: "Бокс",
    },
    {
      question:
        "Какой спортсмен из Кыргызстана выиграл золото на Олимпиаде в Токио?",
      options: [
        "Данияр Кебеков",
        "Бекзат Саттарханов",
        "Равшанбек Джумалиев",
        "Гульмира Маратовна",
      ],
      correctAnswer: "Данияр Кебеков",
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerChange = (e) => {
    setUserAnswer(e.target.value);
  };

  const handleSubmitAnswer = () => {
    if (userAnswer === questions[questionIndex].correctAnswer) {
      setScore(score + 1);
    }

    if (questionIndex + 1 < questions.length) {
      setQuestionIndex(questionIndex + 1);
      setUserAnswer("");
    } else {
      setIsQuizCompleted(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Викторина о Олимпийских Чемпионах Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Пройдите эту викторину, чтобы узнать больше о спортсменах
            Кыргызстана, которые завоевали олимпийские медали!
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      {!isQuizCompleted ? (
        <Box
          sx={{
            textAlign: "center",
            padding: "20px",
            marginTop: "30px",
            animation: "fadeIn 1s ease-out",
          }}
        >
          <Fade in={!isQuizCompleted} timeout={1000}>
            <Box sx={{ maxWidth: "600px", margin: "0 auto" }}>
              <Typography
                variant="h4"
                sx={{
                  fontSize: "32px",
                  marginBottom: "20px",
                  fontWeight: "600",
                  color: "#ff4d6d",
                }}
              >
                {questions[questionIndex].question}
              </Typography>
              <Box>
                {questions[questionIndex].options.map((option, idx) => (
                  <Button
                    key={idx}
                    variant="contained"
                    sx={{
                      display: "block",
                      marginBottom: "10px",
                      width: "100%",
                      backgroundColor: "#121212",
                      color: "white",
                      "&:hover": {
                        backgroundColor: "#ff4d6d",
                      },
                    }}
                    onClick={() => setUserAnswer(option)}
                  >
                    {option}
                  </Button>
                ))}
              </Box>
              <Button
                variant="outlined"
                onClick={handleSubmitAnswer}
                sx={{
                  marginTop: "20px",
                  color: "#ff4d6d",
                  borderColor: "#ff4d6d",
                  "&:hover": {
                    borderColor: "#ff4d6d",
                    backgroundColor: "#ff4d6d",
                    color: "white",
                  },
                }}
              >
                Ответить
              </Button>
            </Box>
          </Fade>
        </Box>
      ) : (
        <Box sx={{ textAlign: "center", padding: "20px", marginTop: "30px" }}>
          <Typography
            variant="h4"
            sx={{ fontSize: "32px", marginBottom: "20px", fontWeight: "600" }}
          >
            Викторина завершена! Ваш результат:
          </Typography>
          <Typography
            variant="h5"
            sx={{ fontSize: "28px", color: "#ff4d6d", fontWeight: "700" }}
          >
            {score} из {questions.length}
          </Typography>
        </Box>
      )}
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

{
