import React, { useState, useEffect } from "react";
import { Button, Box, Typography, TextField } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);

  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [isQuizFinished, setIsQuizFinished] = useState(false);

  const questions = [
    {
      question:
        "Какая страна выиграла самые много медалей на Олимпиаде в Токио?",
      options: ["Казахстан", "Кыргызстан", "Россия", "США"],
      correctAnswer: "США",
    },
    {
      question: "Кто из спортсменов Кыргызстана завоевал золото на Олимпиаде?",
      options: [
        "Данияр Ашиханов",
        "Серик Таранов",
        "Айсулу Тыныбекова",
        "Арсен Кудайбергенов",
      ],
      correctAnswer: "Айсулу Тыныбекова",
    },
    {
      question:
        "В каком виде спорта Кыргызстан имеет сильные позиции на Олимпийских играх?",
      options: ["Бокс", "Футбол", "Легкая атлетика", "Гимнастика"],
      correctAnswer: "Бокс",
    },
  ];

  const handleAnswer = (answer) => {
    if (answer === questions[questionIndex].correctAnswer) {
      setScore(score + 1);
    }
    setUserAnswer(answer);
  };

  const handleNextQuestion = () => {
    if (questionIndex + 1 < questions.length) {
      setQuestionIndex(questionIndex + 1);
      setUserAnswer("");
    } else {
      setIsQuizFinished(true);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      {!isQuizFinished ? (
        <Box
          sx={{
            textAlign: "center",
            padding: "20px",
            backgroundColor: "#fff",
            color: "#121212",
            marginTop: "40px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
          }}
        >
          <Typography variant="h3" sx={{ marginBottom: "20px" }}>
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>
          <Typography variant="h5" sx={{ marginBottom: "20px" }}>
            {questions[questionIndex].question}
          </Typography>

          {questions[questionIndex].options.map((option, index) => (
            <Button
              key={index}
              variant="contained"
              color="primary"
              onClick={() => handleAnswer(option)}
              sx={{
                margin: "10px",
                backgroundColor: "#ff4d6d",
                "&:hover": {
                  backgroundColor: "#ff3366",
                },
              }}
            >
              {option}
            </Button>
          ))}

          <Box sx={{ marginTop: "20px" }}>
            {userAnswer && (
              <Typography
                variant="body1"
                sx={{
                  fontWeight: "bold",
                  color:
                    userAnswer === questions[questionIndex].correctAnswer
                      ? "green"
                      : "red",
                }}
              >
                {userAnswer === questions[questionIndex].correctAnswer
                  ? "Правильный ответ!"
                  : `Неправильный ответ! Правильный ответ: ${questions[questionIndex].correctAnswer}`}
              </Typography>
            )}

            {userAnswer && (
              <Button
                variant="contained"
                color="secondary"
                onClick={handleNextQuestion}
                sx={{
                  marginTop: "10px",
                  backgroundColor: "#4caf50",
                  "&:hover": {
                    backgroundColor: "#45a049",
                  },
                }}
              >
                Следующий вопрос
              </Button>
            )}
          </Box>
        </Box>
      ) : (
        <Box
          sx={{
            textAlign: "center",
            padding: "20px",
            backgroundColor: "#fff",
            color: "#121212",
            marginTop: "40px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
          }}
        >
          <Typography variant="h3" sx={{ marginBottom: "20px" }}>
            Викторина завершена!
          </Typography>
          <Typography variant="h5" sx={{ marginBottom: "20px" }}>
            Ваши очки: {score} из {questions.length}
          </Typography>
        </Box>
      )}
    </Box>
  );
};

export default MainPage;

