import React from "react";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Grid,
  Button,
} from "@mui/material";
import { styled } from "@mui/system";

const StyledCard = styled(Card)({
  marginBottom: "20px",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  transition: "transform 0.3s, box-shadow 0.3s",
  "&:hover": {
    transform: "scale(1.05)",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.2)",
  },
});

const Title = styled(Typography)({
  fontWeight: "bold",
  fontSize: "1.2rem",
  color: "#333",
});

const DateText = styled(Typography)({
  color: "#888",
  fontSize: "0.9rem",
  marginBottom: "8px",
});

const ReadMoreButton = styled(Button)({
  backgroundColor: "#ff4d6d",
  color: "#fff",
  padding: "8px 16px",
  borderRadius: "20px",
  "&:hover": {
    backgroundColor: "#ff1a47",
  },
});

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    date: "30.03.2025",
    image:
      "https://cdn-0.aki.kg/cdn-st-0/qen/M/2559851.bb41ddd5d23b5a8a82c11a6c5cbecadc.jpg",
    link: "https://sport.kg/news/akmataliev-gold",
  },
  {
    id: 2,
    title: "Золото на международных соревнованиях по легкой атлетике",
    date: "18 ноября 2024",
    image: "https://via.placeholder.com/300", // Заглушка
  },
  {
    id: 3,
    title: "Кыргызстанские футболисты готовятся к Кубку Азии 2025",
    date: "12 декабря 2024",
    image: "https://via.placeholder.com/300", // Заглушка
  },
  {
    id: 4,
    title: "Зимние игры 2025: спортсмены Кыргызстана на пути к победе",
    date: "3 января 2025",
    image: "https://via.placeholder.com/300", // Заглушка
  },
];

const NewsPage = () => {
  return (
    <div style={{ padding: "20px", backgroundColor: "#f4f4f4" }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        style={{ marginBottom: "40px" }}
      >
        Новости о спортсменах Кыргызстана (2024-2025)
      </Typography>
      <Grid container spacing={3} justifyContent="center">
        {newsData.map((news) => (
          <Grid item xs={12} md={6} key={news.id}>
            <StyledCard>
              <CardMedia
                component="img"
                height="200"
                image={news.image}
                alt={news.title}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://via.placeholder.com/300";
                }}
              />
              <CardContent>
                <Title>{news.title}</Title>
                <DateText>{news.date}</DateText>
                {!!news.link && (
                  <ReadMoreButton
                    component="a"
                    href={news.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    sx={{ textDecoration: "none" }}
                  >
                    Читать далее
                  </ReadMoreButton>
                )}
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default NewsPage;
