import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ
import natureKR from "../homePage/assets/natureKR.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ
import Confetti from "react-confetti"; // –î–ª—è —ç—Ñ—Ñ–µ–∫—Ç–∞ —Å–∞–ª—é—Ç–∞

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false); // –î–ª—è —Å–∞–ª—é—Ç–∞

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const question = "–ö–∞–∫–æ–π —Å–∞–º—ã–π –≤—ã—Å–æ–∫–∏–π –ø–∏–∫ –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?";
  const answers = ["–ü–∏–∫ –õ–µ–Ω–∏–Ω–∞", "–ü–∏–∫ –ü–æ–±–µ–¥—ã", "–ü–∏–∫ –ú–∞–Ω–∞—Å", "–ü–∏–∫ –ö–æ—Ä–æ–Ω–∞"];
  const correctAnswer = "–ü–∏–∫ –ü–æ–±–µ–¥—ã";

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === correctAnswer);
    if (answer === correctAnswer) {
      setShowConfetti(true); // –ü–æ–∫–∞–∑–∞—Ç—å —Å–∞–ª—é—Ç
      setTimeout(() => setShowConfetti(false), 3000); // –°–∫—Ä—ã—Ç—å —Å–∞–ª—é—Ç —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ —Å —Ñ–æ–Ω–æ–≤—ã–º –≤–∏–¥–µ–æ */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover", // –ò–∑–º–µ–Ω–∏ objectFit
            filter: "none",
            zIndex: -1,
          }}
        />

        {/* –°–∞–ª—é—Ç –ø—Ä–∏ –≤–µ—Ä–Ω–æ–º –æ—Ç–≤–µ—Ç–µ */}
        {showConfetti && (
          <Confetti
            width={window.innerWidth}
            height={window.innerHeight}
            numberOfPieces={500}
            gravity={0.2}
            recycle={false}
          />
        )}

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)", // –ó–∞—Ç–µ–º–Ω—ë–Ω–Ω—ã–π –ø–æ–ª—É–ø—Ä–æ–∑—Ä–∞—á–Ω—ã–π —Ñ–æ–Ω
            borderRadius: "10px", // –°–∫—Ä—É–≥–ª–µ–Ω–∏–µ —É–≥–ª–æ–≤
            padding: "20px",
            width: "100%",
            maxWidth: "600px",
            margin: "0 auto",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.2)", // –¢–µ–Ω—å –¥–ª—è –∫–∞—Ä—Ç–æ—á–∫–∏
            backdropFilter: "blur(10px)", // –≠—Ñ—Ñ–µ–∫—Ç —Ä–∞–∑–º—ã—Ç–∏—è –¥–ª—è –ª—É—á—à–µ–π –∏–Ω—Ç–µ–≥—Ä–∞—Ü–∏–∏ —Å –≤–∏–¥–µ–æ—Ñ–æ–Ω–æ–º
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: "white", marginBottom: "20px" }}
          >
            {question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  transition: "all 0.3s ease",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "rgba(0, 150, 0, 0.7)" // –ü–æ–ª—É–ø—Ä–æ–∑—Ä–∞—á–Ω—ã–π –∑–µ–ª—ë–Ω—ã–π –¥–ª—è –≤–µ—Ä–Ω–æ–≥–æ –æ—Ç–≤–µ—Ç–∞
                        : "rgba(150, 0, 0, 0.7)" // –ü–æ–ª—É–ø—Ä–æ–∑—Ä–∞—á–Ω—ã–π –∫—Ä–∞—Å–Ω—ã–π –¥–ª—è –Ω–µ–≤–µ—Ä–Ω–æ–≥–æ –æ—Ç–≤–µ—Ç–∞
                      : "rgba(255, 255, 255, 0.1)", // –õ—ë–≥–∫–∏–π –ø—Ä–æ–∑—Ä–∞—á–Ω—ã–π —Ü–≤–µ—Ç –¥–ª—è –∫–∞—Ä—Ç–æ—á–µ–∫
                  color: "white",
                  border: "1px solid rgba(255, 255, 255, 0.3)",
                  "&:hover": {
                    transform: "scale(1.05)",
                    backgroundColor: "rgba(255, 255, 255, 0.3)", // –ü–æ–¥—Å–≤–µ—Ç–∫–∞ –ø—Ä–∏ –Ω–∞–≤–µ–¥–µ–Ω–∏–∏
                    filter: "brightness(1.2)", // –Ø—Ä–∫–æ—Å—Ç—å –¥–ª—è –ø–æ–¥—Å–≤–µ—Ç–∫–∏
                  },
                  animation:
                    isCorrect === false && selectedAnswer === answer
                      ? "shake 0.6s"
                      : "none", // –ê–Ω–∏–º–∞—Ü–∏—è –≤–∏–±—Ä–∞—Ü–∏–∏
                }}
              >
                <CardContent>
                  <Typography>{answer}</Typography>
                </CardContent>
              </Card>
            ))}
            {selectedAnswer && (
              <Typography
                sx={{
                  marginTop: "20px",
                  color: isCorrect ? "lightgreen" : "red",
                }}
              >
                {isCorrect ? "–í–µ—Ä–Ω–æ! üèîÔ∏è" : "–ù–µ–≤–µ—Ä–Ω–æ! –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑."}
              </Typography>
            )}
          </Box>
        </Card>
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

{
  /* <div className="main">
<div className="overlay"></div>
<video className="background-video" src={videoBg} autoPlay loop muted />
<div className="content">
  <h4>
    Recipes, books, establishments - everything for chefs and cooking
    lovers. Let's discover new tastes of the world together!
  </h4>
</div>
</div> */
}
// –∞–Ω–∏–º–∞—Ü–∏–∏, –∫—Ä–∞—Å–∏–≤—ã–π –¥–∏–∑–∞–π–Ω –∏ –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏–µ –¥–ª—è –ø—Ä–∏–≤–ª–µ—á–µ–Ω–∏—è –≤–Ω–∏–º–∞–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è, –≤ —ç—Ç–æ–º –∫–æ–¥–µ –∏—Å–ø—Ä–∞–≤—å –≤–∏–∫—Ç–æ—Ä–∏–Ω—É —Å–¥–µ–ª–∞–π –Ω–∞–º–Ω–æ–≥–æ –ª—É—á—à–µ –∏
