import React, { useState, useEffect } from "react";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh", // Видео будет занимать всю высоту экрана
        display: "flex",
        flexDirection: "column", // Структура для видео и изображения в колонку
        justifyContent: "flex-start",
        alignItems: "center",
        fontFamily: "Snell Roundhand, cursive",
      }}
      className="home-main-page"
    >
      {/* Видео фон */}
      <video
        id="background-video"
        className="background-video"
        src={videoBg}
        autoPlay
        loop
        muted
        playsInline
        style={{
          width: "100%",
          height: "auto",
          objectFit: "cover", // Видео будет заполнять весь экран
        }}
      >
        Ваш браузер не поддерживает видео.
      </video>

      {/* Изображение, расположенное внизу видео */}
      <div
        style={{
          width: "100%",
          height: "300px", // Высота изображения (можно изменить)
          backgroundImage: `url("https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg")`,
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundSize: "cover",
        }}
      ></div>

      <div
        style={{
          position: "absolute",
          top: "30%", // Центрируем текст по вертикали
          left: "30%",
          transform: "translate(-50%, -50%)", // Для точного центрирования
          textAlign: "center",
          color: "white",
          padding: "20px",
        }}
        className="content"
      >
        <h1
          style={{
            fontSize: "48px",
            marginBottom: "10px",
            fontWeight: "bold",
            fontFamily: "Georgia, serif",
          }}
        >
          Спортсмены Кыргызстана
        </h1>
        <p style={{ fontSize: "20px", lineHeight: "1.4", fontWeight: "bold" }}>
          Кыргызстан — страна в Центральной Азии, известная своей живописной
          природой, горами Тянь-Шань, кристально чистыми озерами и богатой
          культурой. Здесь проживает около 6,5 миллионов человек, а столица
          страны — Бишкек. В Кыргызстане особенно ценятся традиции,
          гостеприимство и любовь к национальным видам спорта, таким как
          кок-бору и борьба. Сочетая древние традиции и современные достижения,
          Кыргызстан становится популярным местом для туризма и активно
          развивает спорт на мировой арене, в том числе через участие своих
          спортсменов в международных соревнованиях.
        </p>
      </div>
    </div>
  );
};

export default MainPage;

