import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import {
  Typography,
  Card,
  CardContent,
  CardMedia,
  Button,
} from "@mui/material";

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    description: "Впервые в своей карьере!!!",
    date: "21:59, 30.03.2025",
    image:
      "https://prosports.kg/upload/news/main//202503/254179_51d2d45eff3b9e8645a242d95de7a921.webp",
    content:
      "Эрназар Акматалиев завоевал первое место на чемпионате Азии по борьбе в 2025 году. Этот успех стал результатом упорных тренировок и настойчивости.",
  },
  {
    id: 2,
    title: "КПЛ-2025: «Мурас Юнайтед» и «Алай» - лидеры после третьего  тура",
    description:
      "«Мурас Юнайтед» и «Алай» делят первое место в КПЛ-2025 после третьего тура.",
    date: "14:01, 30.03.2025",
    image:
      "https://sport.kg/uploads/posts/2025-03/medium/1743321694_ikonka.jpeg",
    content:
      "В третьем туре КПЛ-2025 «Мурас Юнайтед» и «Алай» по-прежнему остаются на первом месте, что делает чемпионат интересным и непредсказуемым.",
  },
  {
    id: 3,
    title: "Сборная Кыргызстана впервые обыграла действующих чемпионов Азии",
    description: "Двухкратный чемпион Азии бит в Бишкеке со счетом 3:1!",
    date: "10:17, 30.03.2025",
    image:
      "https://prosports.kg/upload/news/content//202503/204165_af5400550094fb8511d1361405ab2930.webp?v=29056305",
    content:
      "Сборная Кыргызстана впервые в истории победила действующих чемпионов Азии. Это событие стало настоящим прорывом для кыргызстанского футбола.",
  },
  {
    id: 4,
    title: "Сестру Валентины Шевченко уволили из UFC",
    description: "Она делает карьеру пилота",
    date: "10:02, 28.03.2025",
    image:
      "https://prosports.kg/upload/news/content//202503/204165_0cc4ccdea0189d29349d1d5cd2ec5d07.webp?v=1743138044548",
    content:
      "Сестра известной бойцы Валентины Шевченко была уволена из UFC, но решила продолжить карьеру в совершенно другой области - в авиации.",
  },
];

const NewsDetailPage1 = () => {
  const { id } = useParams(); // Получаем параметр id из URL
  const newsItem = newsData.find((news) => news.id.toString() === id); // Ищем новость по ID
  const navigate = useNavigate();

  if (!newsItem) {
    return <Typography variant="h6">Новость не найдена</Typography>;
  }

  return (
    <div style={{ padding: "20px", backgroundColor: "#f5f5f5" }}>
      <Button
        onClick={() => navigate("/news")}
        style={{
          marginBottom: "20px",
          backgroundColor: "#ff4d6d",
          color: "#fff",
          "&:hover": {
            backgroundColor: "#ff1a4d",
          },
        }}
      >
        Назад к новостям
      </Button>
      <Card sx={{ maxWidth: 600, margin: "auto" }}>
        <CardMedia
          component="img"
          height="300"
          image={newsItem.image}
          alt={newsItem.title}
        />
        <CardContent>
          <Typography variant="h5" component="div" gutterBottom>
            {newsItem.title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {newsItem.date}
          </Typography>
          <Typography variant="body1" color="text.primary" paragraph>
            {newsItem.content}
          </Typography>
        </CardContent>
      </Card>
    </div>
  );
};

export default NewsDetailPage1;
