import React, { useState } from "react";
import { Button, Box, Typography, TextField, Grid } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [answer, setAnswer] = useState("");
  const [score, setScore] = useState(0);

  const questions = [
    {
      question: "Кто завоевал первую олимпийскую медаль для Кыргызстана?",
      options: ["Айсулу Тыныбекова", "Сеитжан Мамытов", "Джамиля Бейшеналиева"],
      correctAnswer: "Айсулу Тыныбекова",
    },
    {
      question: "В каком году Кыргызстан стал независимым?",
      options: ["1991", "2000", "1995"],
      correctAnswer: "1991",
    },
    {
      question:
        "Какая спортсменка Кыргызстана выиграла бронзу на Олимпийских играх?",
      options: [
        "Айсулу Тыныбекова",
        "Кубаным Жолкушова",
        "Джамиля Бейшеналиева",
      ],
      correctAnswer: "Айсулу Тыныбекова",
    },
  ];

  const handleAnswerChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmitAnswer = () => {
    if (answer === questions[questionIndex].correctAnswer) {
      setScore(score + 1);
    }
    setAnswer("");
    setQuestionIndex((prev) => (prev + 1) % questions.length);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          backgroundColor: "#1a1a1a",
          padding: "40px 20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          borderRadius: "10px",
          boxShadow: "0 6px 12px rgba(0, 0, 0, 0.2)",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            color: "#fff",
            fontSize: "28px",
            fontWeight: "bold",
            marginBottom: "20px",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>
        <Typography
          variant="h5"
          sx={{
            color: "#fff",
            fontSize: "20px",
            marginBottom: "20px",
          }}
        >
          Вопрос {questionIndex + 1} из {questions.length}
        </Typography>
        <Typography
          variant="h6"
          sx={{
            color: "#fff",
            fontSize: "22px",
            fontWeight: "500",
            marginBottom: "20px",
          }}
        >
          {questions[questionIndex].question}
        </Typography>

        <Grid container spacing={2} sx={{ marginBottom: "20px" }}>
          {questions[questionIndex].options.map((option, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Button
                variant="contained"
                color="primary"
                sx={{
                  width: "100%",
                  fontSize: "16px",
                  padding: "15px",
                  borderRadius: "8px",
                  textTransform: "none",
                  backgroundColor: "#ff6f61",
                  "&:hover": {
                    backgroundColor: "#ff3e36",
                  },
                }}
                onClick={() => setAnswer(option)}
              >
                {option}
              </Button>
            </Grid>
          ))}
        </Grid>

        {answer && (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              marginTop: "20px",
            }}
          >
            <Typography
              variant="h6"
              sx={{
                color: "#fff",
                fontSize: "20px",
                fontWeight: "500",
                marginBottom: "10px",
              }}
            >
              Вы выбрали: {answer}
            </Typography>
            <Button
              variant="contained"
              sx={{
                backgroundColor: "#4CAF50",
                "&:hover": { backgroundColor: "#45a049" },
                marginTop: "10px",
              }}
              onClick={handleSubmitAnswer}
            >
              Подтвердить ответ
            </Button>
          </Box>
        )}

        <Typography
          variant="h5"
          sx={{
            color: "#fff",
            fontSize: "20px",
            marginTop: "30px",
          }}
        >
          Ваши очки: {score}
        </Typography>
      </Box>
    </Box>
  );
};

export default MainPage;

