import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  MenuItem,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContextProvider";
import { ADMIN } from "../../helpers/const";

const Navbar = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const { user, handleLogOut, loading } = useAuth();
  const [userRole, setUserRole] = useState("");

  // Проверяем роль пользователя при изменении состояния пользователя
  useEffect(() => {
    if (user) {
      user.getIdTokenResult().then((idTokenResult) => {
        console.log("ID Token:", idTokenResult.claims); // Для диагностики
        if (idTokenResult.claims.admin) {
          setUserRole("admin");
        } else {
          setUserRole("user");
        }
      });
    }
  }, [user]);

  const handleOpenNavMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorEl(null);
  };

  const pages = [
    { id: 1, title: "Атлеты", link: "/athletes" },
    { id: 2, title: "Виды спорта", link: "/sports" },
    { id: 3, title: "Медальный зачет", link: "/medal-table" },
  ];

  // Добавляем страницу "Админ" только для администраторов
  if (userRole === "admin") {
    pages.push({ id: 4, title: "Админ", link: "/admin" });
  }

  return (
    <div style={styles.root}>
      <AppBar position="static" style={styles.appBar}>
        <Toolbar style={styles.toolbar}>
          <div style={styles.leftItems}>
            <img
              id="olympic-rings"
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Flag_of_Kyrgyzstan_%283-2%29.svg/1599px-Flag_of_Kyrgyzstan_%283-2%29.svg.png?20240311163127"
              style={{ ...styles.icon, width: "auto", height: 40 }}
            />
            <Link to={"/"}>
              <Typography
                className="p"
                variant="h6"
                style={{ textDecoration: "none", color: "#ffffff" }}
              >
                Спортсмены КР
              </Typography>
            </Link>
          </div>
          <div style={{ ...styles.centerItems, flex: 1 }}>
            {pages.map((page, index) => (
              <IconButton
                key={page.id}
                color="inherit"
                component={Link}
                to={page.link}
                style={{
                  ...styles.menuButton,
                  marginLeft: index > 0 ? 10 : 0,
                }}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  {page.title}
                </Typography>
              </IconButton>
            ))}
          </div>
          <div style={{ ...styles.rightItems, marginLeft: "auto" }}>
            {!loading && (
              <Typography style={{ color: "#ffffff", marginRight: 10 }}>
                {user
                  ? userRole === "admin"
                    ? "Добро пожаловать, админ"
                    : `Добро пожаловать, ${user.displayName || user.email}`
                  : "Добро пожаловать, гость"}
              </Typography>
            )}
            {user.email === ADMIN ? (
              <Link to={"/admin"} style={{ textDecoration: "none" }}>
                <MenuItem sx={{ color: "white", display: "block" }}>
                  <Typography textAlign={"center"}>ADMIN</Typography>
                </MenuItem>
              </Link>
            ) : null}
            {!loading && !user && (
              <IconButton
                color="inherit"
                component={Link}
                to="/register"
                style={styles.menuButton}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  Регистрация
                </Typography>
              </IconButton>
            )}
            {!loading && user && (
              <IconButton
                color="inherit"
                onClick={handleLogOut}
                style={styles.menuButton}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  Выйти
                </Typography>
              </IconButton>
            )}
          </div>
        </Toolbar>
      </AppBar>
    </div>
  );
};

const styles = {
  root: {
    flexGrow: 1,
    overflow: "hidden",
    width: "100%",
    height: "auto",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
    overflow: "hidden",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    overflow: "hidden",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
  },
  menuButton: {
    marginRight: 10,
    overflow: "hidden",
  },
  menuItem: {
    color: "#ffffff",
  },
  icon: {
    width: "auto",
    height: 40,
    marginRight: 10,
    overflow: "hidden",
  },
};

export default Navbar;
