import React from "react";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  return (
    <div
      style={{
        position: "relative",
        minHeight: "103vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundImage: `url(
          "https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
        )`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        backgroundSize: "cover",
        fontFamily: "Snell Roundhand, cursive",
      }}
      className="home-main-page"
    >
      {/* Видео фон */}
      <video
        className="background-video"
        src={videoBg}
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          objectFit: "cover",
        }}
      >
        Ваш браузер не поддерживает видео.
      </video>

      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.6)", // полупрозрачная черная обводка
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
        className="content"
      >
        <div
          style={{
            maxWidth: "800px",
            padding: "20px",
            textAlign: "center",
            color: "white",
          }}
        >
          {/* <img
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/5c/Olympic_rings_without_rims.svg/2880px-Olympic_rings_without_rims.svg.png"
            alt="Логотип Олимпийских игр"
            style={{
              width: "100%",
              maxWidth: "300px",
              marginBottom: "20px",
              borderRadius: "10px",
            }}
          /> */}
          <h1
            style={{
              fontSize: "48px",
              marginBottom: "10px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
            }}
          >
            Спортсмены Кыргызстана
          </h1>
          {/* <h2
            style={{ fontSize: "36px", marginBottom: "20px", color: "#ffd60a" }}
          >
            Париж 2024
          </h2>
          <p style={{ fontSize: "24px", lineHeight: "1.6", color: "#ffd60a" }}>
            Пройдут с 26 июля по 11 августа 2024 года
          </p> */}
          <p
            style={{ fontSize: "20px", lineHeight: "1.4", fontWeight: "bold" }}
          >
            Кыргызстан — страна в Центральной Азии, известная своей живописной
            природой, горами Тянь-Шань, кристально чистыми озерами и богатой
            культурой. Здесь проживает около 6,5 миллионов человек, а столица
            страны — Бишкек. В Кыргызстане особенно ценятся традиции,
            гостеприимство и любовь к национальным видам спорта, таким как
            кок-бору и борьба. Сочетая древние традиции и современные
            достижения, Кыргызстан становится популярным местом для туризма и
            активно развивает спорт на мировой арене, в том числе через участие
            своих спортсменов в международных соревнованиях.
          </p>
        </div>
      </div>
    </div>
  );
};

export default MainPage;

