import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [quizStarted, setQuizStarted] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const questions = [
    {
      question: "Кто является первым олимпийским чемпионом КР?",
      options: [
        "Александр Большунов",
        "Айсулуу Тыныбекова",
        "Канатбек Бегалиев",
        "Чыңгыз Анарбек уулу",
      ],
      answer: "Айсулуу Тыныбекова",
    },
    {
      question: "В каком виде спорта КР завоевал больше всего медалей?",
      options: ["Борьба", "Лёгкая атлетика", "Бокс", "Дзюдо"],
      answer: "Борьба",
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswer = (option) => {
    if (option === questions[currentQuestion].answer) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      alert(`Викторина окончена! Ваш счет: ${score + 1}/${questions.length}`);
      setQuizStarted(false);
      setCurrentQuestion(0);
      setScore(0);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      <Box sx={{ textAlign: "center", padding: "20px" }}>
        <Typography variant="h4" sx={{ color: "white", fontWeight: "bold" }}>
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>
        {!quizStarted ? (
          <Button
            variant="contained"
            color="primary"
            onClick={() => setQuizStarted(true)}
            sx={{ marginTop: "20px" }}
          >
            Начать викторину
          </Button>
        ) : (
          <Box sx={{ marginTop: "20px", textAlign: "center" }}>
            <Typography variant="h5" sx={{ color: "white" }}>
              {questions[currentQuestion].question}
            </Typography>
            {questions[currentQuestion].options.map((option, index) => (
              <Button
                key={index}
                variant="outlined"
                sx={{ display: "block", margin: "10px auto", color: "white" }}
                onClick={() => handleAnswer(option)}
              >
                {option}
              </Button>
            ))}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

