import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizCompleted, setQuizCompleted] = useState(false);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  const handleOptionClick = (index) => {
    if (index === questions[currentQuestionIndex].correct) {
      setQuizFeedback("Правильный ответ! ✅");
    } else {
      setQuizFeedback("Неправильный ответ. ❌");
    }
    setTimeout(() => {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setQuizFeedback("");
      } else {
        setQuizCompleted(true);
      }
    }, 1500);
  };

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#121212",
        color: "white",
        padding: "20px",
      }}
    >
      <video
        src={videoBg}
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          objectFit: "cover",
          filter: "brightness(50%)",
          zIndex: -1,
        }}
      />

      <Typography
        variant="h2"
        sx={{ marginBottom: "20px", fontWeight: "bold" }}
      >
        Олимпийская Викторина
      </Typography>

      {!quizCompleted ? (
        <Box
          sx={{
            textAlign: "center",
            backgroundColor: "rgba(255, 255, 255, 0.1)",
            padding: "30px",
            borderRadius: "12px",
            boxShadow: "0 4px 10px rgba(255, 255, 255, 0.1)",
          }}
        >
          <Typography variant="h5" sx={{ marginBottom: "20px" }}>
            {questions[currentQuestionIndex].question}
          </Typography>
          <Box sx={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <Button
                key={index}
                variant="contained"
                color="primary"
                sx={{
                  width: "100%",
                  fontSize: "18px",
                  padding: "12px",
                  transition: "transform 0.2s",
                  "&:hover": { transform: "scale(1.05)" },
                }}
                onClick={() => handleOptionClick(index)}
              >
                {option}
              </Button>
            ))}
          </Box>
          {quizFeedback && (
            <Typography
              sx={{ marginTop: "15px", fontSize: "18px", fontWeight: "bold" }}
            >
              {quizFeedback}
            </Typography>
          )}
        </Box>
      ) : (
        <Typography variant="h4" sx={{ textAlign: "center" }}>
          Викторина завершена! 🎉
        </Typography>
      )}
    </Box>
  );
};

export default MainPage;

