import React from "react";
import { Card, CardContent, Typography, Grid, Button } from "@mui/material";
import { styled } from "@mui/system";

// Стилизация с помощью styled API для создания современного и эластичного дизайна
const StyledCard = styled(Card)({
  marginBottom: "20px",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  transition: "transform 0.3s, box-shadow 0.3s",
  "&:hover": {
    transform: "scale(1.05)",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.2)",
  },
});

const Title = styled(Typography)({
  fontWeight: "bold",
  fontSize: "1.2rem",
  color: "#333",
});

const DateText = styled(Typography)({
  color: "#888",
  fontSize: "0.9rem",
});

const Description = styled(Typography)({
  fontSize: "1rem",
  color: "#555",
  marginBottom: "16px",
});

const ReadMoreButton = styled(Button)({
  backgroundColor: "#ff4d6d",
  color: "#fff",
  padding: "8px 16px",
  borderRadius: "20px",
  "&:hover": {
    backgroundColor: "#ff1a47",
  },
});

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    date: "30.03.2025",
    image: "https://example.com/ernazar-akmataliev.jpg",
    link: "https://sport.kg/news/akmataliev-gold",
  },
  {
    id: 2,
    title: "Золото на международных соревнованиях по легкой атлетике",
    date: "18 ноября 2024",
    description:
      "На международных соревнованиях по легкой атлетике спортсмен из Кыргызстана занял первое место, установив новый национальный рекорд.",
  },
  {
    id: 3,
    title: "Кыргызстанские футболисты готовятся к Кубку Азии 2025",
    date: "12 декабря 2024",
    description:
      "Футболисты Кыргызстана intensively готовятся к Кубку Азии 2025, тренировки проходят в лучших спортивных лагерях.",
  },
  {
    id: 4,
    title: "Зимние игры 2025: спортсмены Кыргызстана на пути к победе",
    date: "3 января 2025",
    description:
      "Сборная Кыргызстана готовится к Зимним Олимпийским играм 2025, ожидаются серьезные выступления в горнолыжном спорте и биатлоне.",
  },
];

const NewsPage = () => {
  return (
    <div style={{ padding: "20px", backgroundColor: "#f4f4f4" }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        style={{ marginBottom: "40px" }}
      >
        Новости о спортсменах Кыргызстана (2024-2025)
      </Typography>
      <Grid container spacing={3} justifyContent="center">
        {newsData.map((news) => (
          <Grid item xs={12} md={6} key={news.id}>
            <StyledCard>
              <CardContent>
                <Title>{news.title}</Title>
                <DateText>{news.date}</DateText>
                <Description>{news.description}</Description>
                <ReadMoreButton>Читать далее</ReadMoreButton>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default NewsPage;
