import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ
import { useSpring, animated } from "react-spring"; // –î–ª—è –∞–Ω–∏–º–∞—Ü–∏–∏

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);
  const [currentQuestion, setCurrentQuestion] = useState(0); // –¢–µ–∫—É—â–∏–π –≤–æ–ø—Ä–æ—Å

  const questions = [
    {
      question:
        "–ö—Ç–æ –∏–∑ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–æ–≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ —Å—Ç–∞–ª –æ–ª–∏–º–ø–∏–π—Å–∫–∏–º —á–µ–º–ø–∏–æ–Ω–æ–º –≤ –¢–æ–∫–∏–æ?",
      options: [
        "–ì—É–ª—å–Ω–∞—Ä–∞ –ë–µ–∫–º–∞–º–∞—Ç–æ–≤–∞",
        "–ú–µ–¥–µ—Ç –°–∞–π–¥—É–ª–∞–µ–≤",
        "–£–ª–∞–Ω–±–µ–∫ –ê–º–∏—Ä–æ–≤",
        "–°–∞–∏–¥–∂–æ–Ω –ú–∞–¥–∞–ª–∏–µ–≤",
      ],
      correct: 0,
    },
    {
      question:
        "–ö–∞–∫–æ–π –≤–∏–¥ —Å–ø–æ—Ä—Ç–∞ –ø—Ä–∏–Ω–µ—Å –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω—É –ø–µ—Ä–≤—É—é –æ–ª–∏–º–ø–∏–π—Å–∫—É—é –º–µ–¥–∞–ª—å?",
      options: ["–ë–æ–∫—Å", "–ë–æ—Ä—å–±–∞", "–î–∑—é–¥–æ", "–õ–µ–≥–∫–∞—è –∞—Ç–ª–µ—Ç–∏–∫–∞"],
      correct: 1,
    },
    {
      question:
        "–°–∫–æ–ª—å–∫–æ –º–µ–¥–∞–ª–µ–π –∑–∞–≤–æ–µ–≤–∞–ª–∏ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ –Ω–∞ –û–ª–∏–º–ø–∏–∞–¥–µ 2016 –≥–æ–¥–∞?",
      options: ["1 –º–µ–¥–∞–ª—å", "2 –º–µ–¥–∞–ª–∏", "3 –º–µ–¥–∞–ª–∏", "4 –º–µ–¥–∞–ª–∏"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestion].correct;
    if (index === correctOption) {
      setQuizFeedback("–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ");
      setQuizResults(quizResults + 1);
    } else {
      setQuizFeedback("–ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.");
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowQuiz(false);
      setCurrentQuestion(0); // –°–±—Ä–æ—Å–∏—Ç—å –ø–æ—Å–ª–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –≤–∏–∫—Ç–æ—Ä–∏–Ω—ã
    }
  };

  // –ê–Ω–∏–º–∞—Ü–∏—è –¥–ª—è –≤–∑—Ä—ã–≤–∞ —Å–∞–ª—é—Ç–∞ –ø—Ä–∏ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–º –æ—Ç–≤–µ—Ç–µ
  const animationStyle = useSpring({
    opacity: quizFeedback === "–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ" ? 1 : 0,
    transform:
      quizFeedback === "–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ" ? "scale(1)" : "scale(0)",
    config: { tension: 200, friction: 10 },
  });

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // –°–¥–µ–ª–∞–ª —Ñ–æ–Ω —Ç–µ–º–Ω–µ–µ –¥–ª—è –ª—É—á—à–µ–≥–æ –∫–æ–Ω—Ç—Ä–∞—Å—Ç–∞
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // –ë–æ–ª–µ–µ —Ç–µ–º–Ω—ã–π –æ–≤–µ—Ä–ª–µ–π
          }}
        ></div>
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            marginBottom: "20px",
            fontWeight: "bold",
            color: "#ffcc00",
            fontSize: "28px",
            letterSpacing: "2px",
            textTransform: "uppercase",
            animation: "fadeIn 1s ease-in-out",
          }}
        >
          –í–∏–∫—Ç–æ—Ä–∏–Ω–∞: –û–ª–∏–º–ø–∏–π—Å–∫–∏–µ —á–µ–º–ø–∏–æ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
        </Typography>

        {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ —Ç–µ–∫—É—â–µ–≥–æ –≤–æ–ø—Ä–æ—Å–∞ */}
        <Box sx={{ marginBottom: "30px", position: "relative" }}>
          <Typography
            variant="h5"
            sx={{
              marginBottom: "15px",
              fontSize: "22px",
              fontWeight: "600",
              textAlign: "center",
              color: "#fff",
              animation: "fadeInUp 0.5s ease-out",
            }}
          >
            {questions[currentQuestion].question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column", // –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–æ–µ —Ä–∞—Å–ø–æ–ª–æ–∂–µ–Ω–∏–µ
              gap: "20px",
              alignItems: "center",
            }}
          >
            {questions[currentQuestion].options.map((option, idx) => (
              <Button
                key={idx}
                variant="contained"
                color={selectedOption === idx ? "success" : "primary"}
                onClick={() => handleOptionClick(idx)}
                sx={{
                  background:
                    selectedOption === idx
                      ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                      : "#007bff",
                  transition: "all 0.3s ease",
                  ":hover": {
                    transform: "scale(1.1)",
                    background: "#ff4d6d",
                  },
