import React, { useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import Confetti from "react-confetti"; // Салюты
import videoBg from "../homePage/assets/videoBg.mp4"; // Видео для фона
import natureKR from "../homePage/assets/natureKR.mp4"; // Видео для викторины

const MainPage = () => {
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [isWrongAnswer, setIsWrongAnswer] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [quizFinished, setQuizFinished] = useState(false);

  const questions = [
    {
      question: "Какой самый высокий пик в Кыргызстане?",
      answers: ["Пик Ленина", "Пик Победы", "Пик Манас", "Пик Корона"],
      correctAnswer: "Пик Победы",
    },
    {
      question: "Какой цвет преобладает на флаге Кыргызстана?",
      answers: ["Синий", "Красный", "Зелёный", "Жёлтый"],
      correctAnswer: "Красный",
    },
    {
      question: "Какое национальное блюдо кыргызов?",
      answers: ["Бешбармак", "Лагман", "Манты", "Борщ"],
      correctAnswer: "Бешбармак",
    },
  ];

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    const correct = answer === questions[currentQuestionIndex].correctAnswer;
    setIsCorrect(correct);
    setIsWrongAnswer(!correct);

    if (correct) {
      setScore((prevScore) => prevScore + 1);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 3000);
    } else {
      setTimeout(() => setIsWrongAnswer(false), 1500);
    }

    setTimeout(() => {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedAnswer(null);
        setIsCorrect(null);
      } else {
        setQuizFinished(true);
      }
    }, 2000);
  };

  const handleRestartQuiz = () => {
    setScore(0);
    setCurrentQuestionIndex(0);
    setQuizFinished(false);
    setSelectedAnswer(null);
    setIsCorrect(null);
    setIsWrongAnswer(false);
  };

  const { question, answers } = questions[currentQuestionIndex];

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Заголовок */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{ fontSize: "48px", fontWeight: "bold" }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography variant="body1">
            Спортсмены Кыргызстана завоевывают признание на международной
            арене...
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
        }}
      >
        <video
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            zIndex: -1,
          }}
        />

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)",
            borderRadius: "10px",
            padding: "20px",
            width: "100%",
            maxWidth: "600px",
            margin: "0 auto",
            backdropFilter: "blur(10px)",
          }}
        >
          <Typography variant="h4">{question}</Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  transition: "all 0.3s ease",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "rgba(0, 150, 0, 0.7)"
                        : "rgba(150, 0, 0, 0.7)"
                      : "rgba(255, 255, 255, 0.1)",
                  color: "white",
                  "&:hover": {
                    transform: "scale(1.05)",
                    filter: "brightness(1.2)",
                  },
                }}
                className={isWrongAnswer ? "shake" : ""}
              >
                <CardContent>
                  <Typography>{answer}</Typography>
                </CardContent>
              </Card>
            ))}
            {selectedAnswer && (
              <Typography
                sx={{
                  marginTop: "20px",
                  color: isCorrect ? "lightgreen" : "red",
                }}
              >
                {isCorrect ? "Верно!" : "Неверно! Попробуйте ещё раз."}
              </Typography>
            )}
          </Box>
        </Card>

        {showConfetti && <Confetti />}
        {quizFinished && (
          <Box sx={{ marginTop: "20px" }}>
            <Typography variant="h5">
              Ваш результат: {score} из {questions.length}
            </Typography>
            <Button
              onClick={handleRestartQuiz}
              sx={{
                marginTop: "20px",
                backgroundColor: "#4CAF50",
                color: "#fff",
              }}
            >
              Пройти заново
            </Button>
          </Box>
        )}
      </Box>

      <style>
        {`
          @keyframes shake {
            0% { transform: translateX(0); }
            25% { transform: translateX(-10px); }
            50% { transform: translateX(10px); }
            75% { transform: translateX(-10px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

