import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const quizData = [
  {
    question: "Какой национальный напиток Кыргызстана?",
    options: ["Айран", "Максим", "Кумыс", "Чай"],
    answer: "Кумыс",
  },
  {
    question: "Как называется самая высокая вершина Кыргызстана?",
    options: ["Пик Ленина", "Пик Хан-Тенгри", "Пик Победы", "Пик Манас"],
    answer: "Пик Победы",
  },
  {
    question: "Какое озеро является крупнейшим в Кыргызстане?",
    options: ["Сары-Челек", "Чатыр-Куль", "Сон-Куль", "Иссык-Куль"],
    answer: "Иссык-Куль",
  },
  {
    question: "Какой традиционный вид борьбы популярен в Кыргызстане?",
    options: ["Самбо", "Дзюдо", "Куреш", "Коореш"],
    answer: "Куреш",
  },
  {
    question: "Как называется столица Кыргызстана?",
    options: ["Ош", "Бишкек", "Каракол", "Нарын"],
    answer: "Бишкек",
  },
  {
    question: "Какой валютой пользуются в Кыргызстане?",
    options: ["Сом", "Рубль", "Тенге", "Драм"],
    answer: "Сом",
  },
  {
    question: "Какая традиционная юрта называется в Кыргызстане?",
    options: ["Боз үй", "Кибитка", "Шалаш", "Чум"],
    answer: "Боз үй",
  },
  {
    question: "Как называется национальное конное состязание в Кыргызстане?",
    options: ["Аударыш", "Бузкаши", "Кок-бору", "Тенге алу"],
    answer: "Кок-бору",
  },
  {
    question: "Какая традиционная одежда является символом кыргызского народа?",
    options: ["Шапка", "Чапан", "Калпак", "Кимоно"],
    answer: "Калпак",
  },
  {
    question: "Какая из этих рек протекает через Кыргызстан?",
    options: ["Волга", "Нарын", "Амударья", "Янцзы"],
    answer: "Нарын",
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [quizIndex, setQuizIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isFinished, setIsFinished] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    if (answer === quizData[quizIndex].answer) {
      setScore(score + 1);
    }
    setTimeout(() => {
      if (quizIndex < quizData.length - 1) {
        setQuizIndex(quizIndex + 1);
        setSelectedAnswer(null);
      } else {
        setIsFinished(true);
      }
    }, 1000);
  };

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Кыргызстанские спортсмены завоевывают признание на международной
            арене!
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          padding: "40px 20px",
          backgroundColor: "#121212",
          color: "white",
        }}
      >
        <Typography variant="h4" sx={{ marginBottom: "20px" }}>
          Викторина про Кыргызстан
        </Typography>
        {isFinished ? (
          <Typography variant="h5">
            Вы набрали {score} из {quizData.length} баллов!
          </Typography>
        ) : (
          <Box>
            <Typography variant="h6" sx={{ marginBottom: "15px" }}>
              {quizData[quizIndex].question}
            </Typography>
            {quizData[quizIndex].options.map((option, index) => (
              <Button
                key={index}
                variant="contained"
                sx={{
                  display: "block",
                  margin: "10px auto",
                  backgroundColor:
                    selectedAnswer === option ? "#ff4d6d" : "orange",
                }}
                onClick={() => handleAnswerClick(option)}
              >
                {option}
              </Button>
            ))}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

{
  /* <div className="main">
<div className="overlay"></div>
<video className="background-video" src={videoBg} autoPlay loop muted />
<div className="content">
  <h4>
    Recipes, books, establishments - everything for chefs and cooking
    lovers. Let's discover new tastes of the world together!
  </h4>
</div>
</div> */
}
