import React, { useState, useEffect } from "react";
import { Box, Typography, Button } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Confetti from "react-confetti";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isQuizFinished, setIsQuizFinished] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState({ correct: 0, incorrect: 0 });
  const [showConfetti, setShowConfetti] = useState(false);

  const questions = [
    {
      question:
        "Какой спортсмен Кыргызстана завоевал золото на Олимпийских играх?",
      options: [
        "Данияр Кенешов",
        "Ислам Бейшеналиев",
        "Чингиз Маматов",
        "Асылбек Жекшенкулов",
      ],
      answer: "Ислам Бейшеналиев",
    },
    {
      question:
        "В каком виде спорта Кыргызстан завоевал медали на Олимпиаде 2020 года?",
      options: ["Бокс", "Футбол", "Гимнастика", "Велоспорт"],
      answer: "Бокс",
    },
    // Добавьте другие вопросы
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerSelect = (option) => {
    setSelectedOption(option);
    const currentQuestion = questions[questionIndex];
    const correct = option === currentQuestion.answer;
    setIsCorrect(correct);

    if (correct) {
      setScore((prevScore) => ({
        ...prevScore,
        correct: prevScore.correct + 1,
      }));
    } else {
      setScore((prevScore) => ({
        ...prevScore,
        incorrect: prevScore.incorrect + 1,
      }));
    }

    setTimeout(() => {
      if (questionIndex + 1 < questions.length) {
        setQuestionIndex(questionIndex + 1);
        setSelectedOption(null);
        setIsCorrect(null);
      } else {
        setIsQuizFinished(true);
        setShowConfetti(true);
      }
    }, 1000);
  };

  const handleRestartQuiz = () => {
    setScore({ correct: 0, incorrect: 0 });
    setIsQuizFinished(false);
    setQuestionIndex(0);
    setSelectedOption(null);
    setIsCorrect(null);
    setShowConfetti(false);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(60%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box sx={{ marginTop: "50px", textAlign: "center", color: "white" }}>
        {!isQuizFinished ? (
          <div className="quiz-container">
            <h3 className="question">{questions[questionIndex].question}</h3>
            <div className="options">
              {questions[questionIndex].options.map((option, index) => (
                <Button
                  key={index}
                  variant="outlined"
                  color={
                    selectedOption === option
                      ? isCorrect
                        ? "success"
                        : "error"
                      : "primary"
                  }
                  onClick={() => handleAnswerSelect(option)}
                >
                  {option}
                </Button>
              ))}
            </div>

            {isCorrect !== null && (
              <Typography variant="h6" color={isCorrect ? "success" : "error"}>
                {isCorrect ? "Правильно!" : "Неправильно, попробуйте снова."}
              </Typography>
            )}

            {showConfetti && <Confetti />}
          </div>
        ) : (
          <div className="quiz-results">
            <h3>Викторина завершена!</h3>
            <p>Правильных ответов: {score.correct}</p>
            <p>Неправильных ответов: {score.incorrect}</p>
            <Button
              variant="contained"
              color="primary"
              onClick={handleRestartQuiz}
            >
              Начать снова
            </Button>
          </div>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

