import React, { useState } from "react";
import { Button, Box, Typography, Fade } from "@mui/material";
import { keyframes } from "@mui/system";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showQuiz, setShowQuiz] = useState(false);
  const [score, setScore] = useState(0);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана выиграл золото на Олимпиаде 2016?",
      options: [
        "Айсулу Тыныбекова",
        "Сымбат Ахметов",
        "Рустам Аширов",
        "Нуржигит Ниязбеков",
      ],
      answer: "Айсулу Тыныбекова",
    },
    {
      question:
        "Какой спортсмен Кыргызстана завоевал серебро на Олимпиаде 2020?",
      options: [
        "Атай Токтоналиев",
        "Мирбек Айткулов",
        "Айсулу Тыныбекова",
        "Бекболот Талантбеков",
      ],
      answer: "Айсулу Тыныбекова",
    },
    {
      question: "Кто был первым олимпийским чемпионом Кыргызстана?",
      options: [
        "Азиз Абдразаков",
        "Тамирлан Токаев",
        "Данияр Хасанов",
        "Тамара Таташева",
      ],
      answer: "Азиз Абдразаков",
    },
  ];

  const handleAnswer = (option) => {
    if (option === questions[currentQuestion].answer) {
      setScore(score + 1);
    }
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert(
        `Викторина завершена! Ваш результат: ${score} из ${questions.length}`
      );
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: showQuiz ? 1 : 0,
          transform: showQuiz ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Угадай олимпийских чемпионов Кыргызстана!
          </Typography>

          {/* Кнопка запуска викторины */}
          <Button
            variant="contained"
            color="primary"
            sx={{
              marginTop: "20px",
              padding: "10px 20px",
              fontSize: "18px",
              fontWeight: "bold",
              backgroundColor: "#ff4d6d",
              "&:hover": {
                backgroundColor: "#ff1f45",
              },
            }}
            onClick={() => setShowQuiz(true)}
          >
            Начать викторину
          </Button>
        </Box>
      </Box>

      {/* Викторина */}
      {showQuiz && (
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            padding: "20px",
            backgroundColor: "#333",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            maxWidth: "600px",
            margin: "30px auto",
            opacity: 1,
            transform: "translateY(0)",
            transition: "all 1s ease-out",
          }}
        >
          <Typography
            variant="h3"
            sx={{
              fontSize: "24px",
              color: "#fff",
              marginBottom: "20px",
              fontWeight: "bold",
              letterSpacing: "1px",
            }}
          >
            {questions[currentQuestion].question}
          </Typography>

          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {questions[currentQuestion].options.map((option, index) => (
              <Button
                key={index}
                variant="outlined"
                sx={{
                  marginBottom: "10px",
                  fontSize: "18px",
                  padding: "10px 20px",
                  borderRadius: "8px",
                  color: "#fff",
                  borderColor: "#ff4d6d",
                  "&:hover": {
                    borderColor: "#ff1f45",
                    backgroundColor: "#ff4d6d",
                  },
                }}
                onClick={() => handleAnswer(option)}
              >
                {option}
              </Button>
            ))}
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default MainPage;

