import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [questionIndex, setQuestionIndex] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[questionIndex].correct; // Проверка текущего вопроса
    if (index === correctOption) {
      setQuizFeedback("Правильный ответ!");
      setTimeout(() => {
        setQuizFeedback("");
        setQuestionIndex((prevIndex) => prevIndex + 1); // Переход к следующему вопросу
        setSelectedOption(null);
        setIsAnswered(false); // Сбросить состояние после ответа
      }, 2000); // Задержка, чтобы показать сообщение
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова.");
      setIsAnswered(true); // Показать ошибку
    }
  };

  const handleNextQuestion = () => {
    setIsAnswered(false); // Сбросить состояние ответа
    setSelectedOption(null);
    setQuizFeedback("");
    setQuestionIndex(questionIndex + 1); // Перейти к следующему вопросу
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Пройдите викторину, чтобы узнать больше о спортсменах и их
            достижениях на Олимпийских играх!
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            marginBottom: "20px",
            fontWeight: "bold",
            color: "#ffcc00",
            fontSize: "28px",
            letterSpacing: "2px",
            textTransform: "uppercase",
            animation: "fadeIn 1s ease-in-out",
          }}
        >
          {questions[questionIndex]?.question}
        </Typography>
        <Box
          sx={{
            display: "flex",
            flexDirection: "column", // Вертикальное расположение
            gap: "20px",
            alignItems: "center",
          }}
        >
          {questions[questionIndex]?.options.map((option, idx) => (
            <Button
              key={idx}
              variant="contained"
              color={selectedOption === idx ? "success" : "primary"}
              onClick={() => handleOptionClick(idx)}
              sx={{
                background:
                  selectedOption === idx
                    ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                    : "#007bff",
                transition: "all 0.3s ease",
                ":hover": {
                  transform: "scale(1.1)",
                  background: "#ff4d6d",
                },
                padding: "15px 30px",
                borderRadius: "10px",
                fontSize: "16px",
                boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              }}
            >
              {option}
            </Button>
          ))}
        </Box>
        {quizFeedback && (
          <Typography
            sx={{
              marginTop: "20px",
              fontWeight: "bold",
              fontSize: "18px",
              color: quizFeedback === "Правильный ответ!" ? "green" : "red",
              animation: "fadeIn 0.5s ease-out",
              transform:
                quizFeedback === "Правильный ответ!"
                  ? "scale(1.2)"
                  : "scale(1)",
              transition: "transform 0.3s ease-in-out",
            }}
          >
            {quizFeedback}
          </Typography>
        )}
        {isAnswered && questionIndex < questions.length - 1 && (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleNextQuestion}
            sx={{
              marginTop: "30px",
              padding: "12px 20px",
              fontSize: "18px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease",
              ":hover": { transform: "scale(1.05)" },
            }}
          >
            Перейти к следующему вопросу
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

