import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ

const MainPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [showNextButton, setShowNextButton] = useState(false);

  const questions = [
    {
      question:
        "–ö—Ç–æ –∏–∑ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–æ–≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ —Å—Ç–∞–ª –æ–ª–∏–º–ø–∏–π—Å–∫–∏–º —á–µ–º–ø–∏–æ–Ω–æ–º –≤ –¢–æ–∫–∏–æ?",
      options: [
        "–ì—É–ª—å–Ω–∞—Ä–∞ –ë–µ–∫–º–∞–º–∞—Ç–æ–≤–∞",
        "–ú–µ–¥–µ—Ç –°–∞–π–¥—É–ª–∞–µ–≤",
        "–£–ª–∞–Ω–±–µ–∫ –ê–º–∏—Ä–æ–≤",
        "–°–∞–∏–¥–∂–æ–Ω –ú–∞–¥–∞–ª–∏–µ–≤",
      ],
      correct: 0,
    },
    {
      question:
        "–ö–∞–∫–æ–π –≤–∏–¥ —Å–ø–æ—Ä—Ç–∞ –ø—Ä–∏–Ω–µ—Å –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω—É –ø–µ—Ä–≤—É—é –æ–ª–∏–º–ø–∏–π—Å–∫—É—é –º–µ–¥–∞–ª—å?",
      options: ["–ë–æ–∫—Å", "–ë–æ—Ä—å–±–∞", "–î–∑—é–¥–æ", "–õ–µ–≥–∫–∞—è –∞—Ç–ª–µ—Ç–∏–∫–∞"],
      correct: 1,
    },
    {
      question:
        "–°–∫–æ–ª—å–∫–æ –º–µ–¥–∞–ª–µ–π –∑–∞–≤–æ–µ–≤–∞–ª–∏ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ –Ω–∞ –û–ª–∏–º–ø–∏–∞–¥–µ 2016 –≥–æ–¥–∞?",
      options: ["1 –º–µ–¥–∞–ª—å", "2 –º–µ–¥–∞–ª–∏", "3 –º–µ–¥–∞–ª–∏", "4 –º–µ–¥–∞–ª–∏"],
      correct: 1,
    },
  ];

  const handleOptionClick = (index) => {
    const correctOption = questions[currentQuestionIndex].correct;

    setSelectedOption(index);

    if (index === correctOption) {
      setQuizFeedback("–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ");
    } else {
      setQuizFeedback("–ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞. ‚ùå");
    }

    setShowNextButton(true); // –ü–æ–∫–∞–∑–∞—Ç—å –∫–Ω–æ–ø–∫—É –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É –≤–æ–ø—Ä–æ—Å—É
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    setShowNextButton(false);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1); // –ü–µ—Ä–µ—Ö–æ–¥ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É –≤–æ–ø—Ä–æ—Å—É
    } else {
      setQuizFeedback("–í–∏–∫—Ç–æ—Ä–∏–Ω–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞! –°–ø–∞—Å–∏–±–æ –∑–∞ —É—á–∞—Å—Ç–∏–µ. üéâ"); // –ö–æ–Ω–µ—Ü –≤–∏–∫—Ç–æ—Ä–∏–Ω—ã
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç —Å –∞–Ω–∏–º–∞—Ü–∏–µ–π */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: 1,
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            –í–∏–∫—Ç–æ—Ä–∏–Ω–∞: –û–ª–∏–º–ø–∏–π—Å–∫–∏–µ —á–µ–º–ø–∏–æ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
          </Typography>
        </Box>
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        {questions.length > 0 && (
          <Box key={currentQuestionIndex} sx={{ marginBottom: "30px" }}>
            <Typography
              variant="h5"
              sx={{
                marginBottom: "15px",
                fontSize: "22px",
                fontWeight: "600",
                textAlign: "center",
                color: "#fff",
                animation: "fadeInUp 0.5s ease-out",
              }}
            >
              {questions[currentQuestionIndex].question}
            </Typography>
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                gap: "15px",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              {questions[currentQuestionIndex].options.map((option, idx) => (
                <Button
                  key={idx}
                  variant="contained"
                  color={selectedOption === idx ? "success" : "primary"}
                  onClick={() => handleOptionClick(idx)}
                  sx={{
                    background:
                      selectedOption === idx
                        ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                        : "#007bff",
                    transition: "all 0.3s ease",
                    ":hover": {
                      transform: "scale(1.1)",
                      background: "#ff4d6d",
                    },
                    padding: "15px 30px",
                    borderRadius: "10px",
                    fontSize: "16px",
                    boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                  }}
                >
                  {option}
                </Button>
              ))}
            </Box>

            {/* –ü–æ–∫–∞–∑–∞—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç */}
            <Typography
              sx={{
                marginTop: "20px",
                fontWeight: "bold",
                fontSize: "20px",
                color:
                  quizFeedback === "–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ" ? "green" : "red",
                animation: "fadeIn 0.5s ease-out",
              }}
            >
              {quizFeedback}
            </Typography>

            {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É –≤–æ–ø—Ä–æ—Å—É */}
            {showNextButton && (
              <Button
                variant="contained"
                color="secondary"
                onClick={handleNextQuestion}
                sx={{
                  marginTop: "30px",
                  padding: "12px 20px",
                  fontSize: "18px",
                  borderRadius: "10px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                  transition: "transform 0.3s ease",
                  ":hover": { transform: "scale(1.05)" },
                }}
              >
                –°–ª–µ–¥—É—é—â–∏–π –≤–æ–ø—Ä–æ—Å
              </Button>
            )}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

