import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [showNextButton, setShowNextButton] = useState(false);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  const handleOptionClick = (index) => {
    const correctOption = questions[currentQuestionIndex].correct;

    setSelectedOption(index);

    if (index === correctOption) {
      setQuizFeedback("Правильный ответ! 🎉");
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова. ❌");
    }

    setShowNextButton(true); // Показать кнопку для перехода к следующему вопросу
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    setShowNextButton(false);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1); // Переход к следующему вопросу
    } else {
      setQuizFeedback("Викторина завершена! Спасибо за участие. 🎉"); // Конец викторины
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: 1,
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        {questions.length > 0 && (
          <Box key={currentQuestionIndex} sx={{ marginBottom: "30px" }}>
            <Typography
              variant="h5"
              sx={{
                marginBottom: "15px",
                fontSize: "22px",
                fontWeight: "600",
                textAlign: "center",
                color: "#fff",
                animation: "fadeInUp 0.5s ease-out",
              }}
            >
              {questions[currentQuestionIndex].question}
            </Typography>
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                gap: "15px",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              {questions[currentQuestionIndex].options.map((option, idx) => (
                <Button
                  key={idx}
                  variant="contained"
                  color={selectedOption === idx ? "success" : "primary"}
                  onClick={() => handleOptionClick(idx)}
                  sx={{
                    background:
                      selectedOption === idx
                        ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                        : "#007bff",
                    transition: "all 0.3s ease",
                    ":hover": {
                      transform: "scale(1.1)",
                      background: "#ff4d6d",
                    },
                    padding: "15px 30px",
                    borderRadius: "10px",
                    fontSize: "16px",
                    boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                  }}
                >
                  {option}
                </Button>
              ))}
            </Box>

            {/* Показать результат */}
            <Typography
              sx={{
                marginTop: "20px",
                fontWeight: "bold",
                fontSize: "20px",
                color:
                  quizFeedback === "Правильный ответ! 🎉" ? "green" : "red",
                animation: "fadeIn 0.5s ease-out",
              }}
            >
              {quizFeedback}
            </Typography>

            {/* Кнопка для перехода к следующему вопросу */}
            {showNextButton && (
              <Button
                variant="contained"
                color="secondary"
                onClick={handleNextQuestion}
                sx={{
                  marginTop: "30px",
                  padding: "12px 20px",
                  fontSize: "18px",
                  borderRadius: "10px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                  transition: "transform 0.3s ease",
                  ":hover": { transform: "scale(1.05)" },
                }}
              >
                Следующий вопрос
              </Button>
            )}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

