import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import Confetti from "react-confetti"; // –ü–æ–¥–∫–ª—é—á–∞–µ–º –±–∏–±–ª–∏–æ—Ç–µ–∫—É –¥–ª—è —Å–∞–ª—é—Ç–æ–≤
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ
import natureKR from "../homePage/assets/natureKR.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false); // –î–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è —Å–∞–ª—é—Ç–æ–≤
  const [isWrongAnswer, setIsWrongAnswer] = useState(false); // –î–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è –Ω–µ–ø—Ä–∞–≤–∏–ª—å–Ω–æ–≥–æ –æ—Ç–≤–µ—Ç–∞
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // –ò–Ω–¥–µ–∫—Å —Ç–µ–∫—É—â–µ–≥–æ –≤–æ–ø—Ä–æ—Å–∞

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const questions = [
    {
      question: "–ö–∞–∫–æ–π —Å–∞–º—ã–π –≤—ã—Å–æ–∫–∏–π –ø–∏–∫ –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?",
      answers: ["–ü–∏–∫ –õ–µ–Ω–∏–Ω–∞", "–ü–∏–∫ –ü–æ–±–µ–¥—ã", "–ü–∏–∫ –ú–∞–Ω–∞—Å", "–ü–∏–∫ –ö–æ—Ä–æ–Ω–∞"],
      correctAnswer: "–ü–∏–∫ –ü–æ–±–µ–¥—ã",
    },
    {
      question: "–ö–∞–∫–æ–µ –Ω–∞—Ü–∏–æ–Ω–∞–ª—å–Ω–æ–µ –±–ª—é–¥–æ –∫—ã—Ä–≥—ã–∑–æ–≤?",
      answers: ["–ë–µ—à–±–∞—Ä–º–∞–∫", "–õ–∞–≥–º–∞–Ω", "–ú–∞–Ω—Ç—ã", "–ñ–∞—à—ã–ª—ã–∫"],
      correctAnswer: "–ë–µ—à–±–∞—Ä–º–∞–∫",
    },
    {
      question: "–ö–∞–∫–æ–π –Ω–∞–ø–∏—Ç–æ–∫ —è–≤–ª—è–µ—Ç—Å—è —Ç—Ä–∞–¥–∏—Ü–∏–æ–Ω–Ω—ã–º –¥–ª—è –∫—ã—Ä–≥—ã–∑–æ–≤?",
      answers: ["–ö—É–º—ã—Å", "–ê–π—Ä–∞–Ω", "–®—É–±–∞—Ç", "–ß–∞–π"],
      correctAnswer: "–ö—É–º—ã—Å",
    },
    {
      question: "–ö–æ–≥–¥–∞ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω –æ–±—ä—è–≤–∏–ª —Å–≤–æ—é –Ω–µ–∑–∞–≤–∏—Å–∏–º–æ—Å—Ç—å?",
      answers: ["1991 –≥–æ–¥", "1995 –≥–æ–¥", "1989 –≥–æ–¥", "2000 –≥–æ–¥"],
      correctAnswer: "1991 –≥–æ–¥",
    },
    {
      question: "–ö–∞–∫–∞—è —Å—Ç–æ–ª–∏—Ü–∞ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞?",
      answers: ["–ë–∏—à–∫–µ–∫", "–û—à", "–ù–∞—Ä—ã–Ω", "–¢–∞–ª–∞—Å"],
      correctAnswer: "–ë–∏—à–∫–µ–∫",
    },
    {
      question: "–ö–∞–∫–æ–π —è–∑—ã–∫ —è–≤–ª—è–µ—Ç—Å—è –≥–æ—Å—É–¥–∞—Ä—Å—Ç–≤–µ–Ω–Ω—ã–º –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?",
      answers: ["–†—É—Å—Å–∫–∏–π", "–ö–∞–∑–∞—Ö—Å–∫–∏–π", "–ö—ã—Ä–≥—ã–∑—Å–∫–∏–π", "–¢–∞–¥–∂–∏–∫—Å–∫–∏–π"],
      correctAnswer: "–ö—ã—Ä–≥—ã–∑—Å–∫–∏–π",
    },
    {
      question: "–ö–∞–∫–∞—è –≤–∞–ª—é—Ç–∞ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?",
      answers: ["–¢–µ–Ω–≥–µ", "–†—É–±–ª—å", "–°–æ–º", "–î–æ–ª–ª–∞—Ä"],
      correctAnswer: "–°–æ–º",
    },
    {
      question:
        "–ö–∞–∫ –Ω–∞–∑—ã–≤–∞–µ—Ç—Å—è –Ω–∞—Ü–∏–æ–Ω–∞–ª—å–Ω—ã–π –ø—Ä–∞–∑–¥–Ω–∏–∫ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞, –ø–æ—Å–≤—è—â—ë–Ω–Ω—ã–π –Ω–µ–∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏?",
      answers: ["–ù–æ–æ—Ä—É–∑", "–î–µ–Ω—å –Ω–µ–∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏", "–ú–∞–Ω–∞—Å", "–û—à"],
      correctAnswer: "–î–µ–Ω—å –Ω–µ–∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏",
    },
    {
      question:
        "–ö–∞–∫–∞—è –≥–æ—Ä–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞ –∑–∞–Ω–∏–º–∞–µ—Ç –±–æ–ª—å—à—É—é —á–∞—Å—Ç—å —Ç–µ—Ä—Ä–∏—Ç–æ—Ä–∏–∏ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞?",
      answers: ["–ì–æ—Ä—ã –¢—è–Ω—å-–®–∞–Ω—è", "–ö–∞–≤–∫–∞–∑", "–£—Ä–∞–ª", "–ê–ª—å–ø—ã"],
      correctAnswer: "–ì–æ—Ä—ã –¢—è–Ω—å-–®–∞–Ω—è",
    },
    {
      question: "–ö–∞–∫–æ–µ –∂–∏–≤–æ—Ç–Ω–æ–µ —è–≤–ª—è–µ—Ç—Å—è —Å–∏–º–≤–æ–ª–æ–º –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞?",
      answers: ["–û–≤—Ü–∞", "–õ–æ—à–∞–¥—å", "–ö–æ–∑—ë–ª", "–Ø–∫"],
      correctAnswer: "–õ–æ—à–∞–¥—å",
    },
  ];

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    const correct = answer === correctAnswer;
    setIsCorrect(correct);
    setIsWrongAnswer(!correct);
    if (correct) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000); // –°–∞–ª—é—Ç –±—É–¥–µ—Ç –æ—Ç–æ–±—Ä–∞–∂–∞—Ç—å—Å—è 5 —Å–µ–∫—É–Ω–¥
    } else {
      setTimeout(() => setIsWrongAnswer(false), 1500); // –£–±–∏—Ä–∞–µ–º –∞–Ω–∏–º–∞—Ü–∏—é —á–µ—Ä–µ–∑ 1.5 —Å–µ–∫—É–Ω–¥—ã
    }
  };

  const { question, answers } = questions[currentQuestionIndex];

  return (
    <Box
