import React, { useState } from "react";
import { useAuth } from "../components/context/AuthContextProvider";
import { useNavigate } from "react-router-dom";
import "../pages/Auth.css";

const Auth = () => {
  const {
    email,
    setEmail,
    password,
    setPassword,
    handleRegister,
    handleLogin,
    emailError,
    passwordError,
    hasAccount,
    setHasAccount,
  } = useAuth();

  const [confirmPassword, setConfirmPassword] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");
  const navigate = useNavigate();

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();

    if (hasAccount) {
      handleLogin(); // Для входа
    } else {
      if (password !== confirmPassword) {
        setConfirmPasswordError("Пароли не совпадают");
        return;
      }
      handleRegister(); // Для регистрации
    }
  };

  return (
    <div className="auth-container">
      <div className="left-section">
        <video autoPlay loop muted preload="auto">
          <source src="/video/Flag_of_the_KR.mp4" type="video/mp4" />
          Ваш браузер не поддерживает видео.
        </video>
      </div>

      <div className="right-section">
        <div className="auth-form">
          <h2 className="auth-title">{hasAccount ? "Вход" : "Регистрация"}</h2>
          <form onSubmit={handleSubmit}>
            {!hasAccount && (
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Ваше имя"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
            )}
            <div className="input-group">
              <input
                type="email"
                placeholder="Электронная почта"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <p className="error-msg">{emailError}</p>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="Пароль"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <p className="error-msg">{passwordError}</p>
            </div>
            {!hasAccount && (
              <div className="input-group">
                <input
                  type="password"
                  placeholder="Подтвердите пароль"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
                <p className="error-msg">{confirmPasswordError}</p>
              </div>
            )}
            <div className="btn-container">
              <button type="submit">
                {hasAccount ? "Войти" : "Регистрация"}
              </button>
              <p>
                {hasAccount ? "Нет аккаунта?" : "Уже есть аккаунт?"}{" "}
                <span onClick={() => setHasAccount(!hasAccount)}>
                  {hasAccount ? "Регистрация" : "Вход"}
                </span>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Auth;
