import React, { useState } from "react";
import { useProduct } from "../context/ProductContextProvider";
import { Box, TextField, Button } from "@mui/material";

const AddCategory = () => {
  const { createSport } = useProduct();
  const [sport, setSport] = useState({
    name: "",
  });

  const handleChange = (e) => {
    setSport({ ...sport, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createSport(sport);
    setSport({ ...sport, name: "" });
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        width: "90%",
        maxWidth: "310px", // Ограничиваем максимальную ширину
        padding: 2,
        border: "1px solid black",
        borderRadius: 1,
        backgroundColor: "#b5ffe1",
        color: "#01263a",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        margin: "100px auto", // Центрируем элемент
        position: "relative",
        top: "350px", // Уменьшил top, чтобы не было слишком большого отступа

        "@media (max-width: 1200px)": {
          width: "80%",
        },
        "@media (max-width: 992px)": {
          width: "70%",
        },
        "@media (max-width: 768px)": {
          width: "60%",
        },
        "@media (max-width: 480px)": {
          width: "80%",
        },
        "@media (max-width: 320px)": {
          width: "85%",
        },
      }}
    >
      <TextField
        type="text"
        name="name"
        value={sport.name}
        onChange={handleChange}
        label="Sport Name"
        variant="outlined"
        fullWidth
        sx={{ marginBottom: "16px", backgroundColor: "#ADE8FE" }}
      />
      <Button type="submit" variant="contained" color="primary">
        Add Category
      </Button>
    </Box>
  );
};

export default AddCategory;
