import React, { useState } from "react";
import { Box, Typography, Button } from "@mui/material";
import Confetti from "react-confetti";

const MainPage = () => {
  const [isQuizFinished, setIsQuizFinished] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState({ correct: 0, incorrect: 0 });
  const [showConfetti, setShowConfetti] = useState(false);

  const questions = [
    {
      question: "Какой вид спорта стал популярным в Кыргызстане?",
      options: ["Бокс", "Футбол", "Теннис", "Гольф"],
      answer: "Бокс",
    },
    {
      question: "Какая страна является конкурентом Кыргызстана в борьбе?",
      options: ["Россия", "США", "Китай", "Япония"],
      answer: "Россия",
    },
    {
      question: "Какие спортсмены Кыргызстана известны на международной арене?",
      options: [
        "Чемпионы по борьбе",
        "Чемпионы по плаванию",
        "Чемпионы по футболу",
        "Чемпионы по теннису",
      ],
      answer: "Чемпионы по борьбе",
    },
    // Добавьте другие вопросы по необходимости
  ];

  const handleAnswerSelect = (option) => {
    if (selectedOption !== null) return; // Запрещаем выбирать новый ответ до обработки предыдущего
    setSelectedOption(option);
    const correct = option === questions[questionIndex].answer;
    setIsCorrect(correct);
    if (correct) {
      setScore({ ...score, correct: score.correct + 1 });
    } else {
      setScore({ ...score, incorrect: score.incorrect + 1 });
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setIsCorrect(null);
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
    } else {
      setIsQuizFinished(true);
      setShowConfetti(true);
    }
  };

  const handleRestartQuiz = () => {
    setQuestionIndex(0);
    setScore({ correct: 0, incorrect: 0 });
    setIsQuizFinished(false);
    setShowConfetti(false);
  };

  return (
    <Box className="home-main-page">
      {/* Видео фон и остальной контент страницы */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
          }}
        />
      </Box>

      {/* Викторина */}
      {!isQuizFinished ? (
        <div className="quiz-container">
          <h3 className="question">{questions[questionIndex].question}</h3>
          <div className="options">
            {questions[questionIndex].options.map((option, index) => (
              <button
                key={index}
                className={`option-button ${
                  selectedOption === option
                    ? isCorrect
                      ? "correct"
                      : "incorrect"
                    : ""
                }`}
                onClick={() => handleAnswerSelect(option)}
              >
                {option}
              </button>
            ))}
          </div>

          {isCorrect !== null && (
            <p className={`feedback ${isCorrect ? "correct" : "incorrect"}`}>
              {isCorrect ? "Правильно!" : "Неправильно, попробуйте снова."}
            </p>
          )}

          <Button
            sx={{ marginTop: "20px" }}
            variant="contained"
            color="primary"
            onClick={handleNextQuestion}
          >
            {questionIndex < questions.length - 1
              ? "Следующий вопрос"
              : "Закончить викторину"}
          </Button>

          {showConfetti && <Confetti />}
        </div>
      ) : (
        <div className="quiz-results">
          <h3>Викторина завершена!</h3>
          <p>Правильные ответы: {score.correct}</p>
          <p>Неправильные ответы: {score.incorrect}</p>
          <Button
            variant="contained"
            color="primary"
            sx={{ marginTop: "20px" }}
            onClick={handleRestartQuiz}
          >
            Начать заново
          </Button>
        </div>
      )}
    </Box>
  );
};

export default MainPage;

