import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ
import natureKR from "../homePage/assets/natureKR.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const question = "–ö–∞–∫–æ–π —Å–∞–º—ã–π –≤—ã—Å–æ–∫–∏–π –ø–∏–∫ –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?";
  const answers = ["–ü–∏–∫ –õ–µ–Ω–∏–Ω–∞", "–ü–∏–∫ –ü–æ–±–µ–¥—ã", "–ü–∏–∫ –ú–∞–Ω–∞—Å", "–ü–∏–∫ –ö–æ—Ä–æ–Ω–∞"];
  const correctAnswer = "–ü–∏–∫ –ü–æ–±–µ–¥—ã";

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === correctAnswer);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(85%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç —Å –∞–Ω–∏–º–∞—Ü–∏–µ–π */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            –°–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            –°–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ –∑–∞–≤–æ–µ–≤—ã–≤–∞—é—Ç –ø—Ä–∏–∑–Ω–∞–Ω–∏–µ –Ω–∞ –º–µ–∂–¥—É–Ω–∞—Ä–æ–¥–Ω–æ–π –∞—Ä–µ–Ω–µ
            –≤ —Ç–∞–∫–∏—Ö –≤–∏–¥–∞—Ö —Å–ø–æ—Ä—Ç–∞, –∫–∞–∫ –±–æ—Ä—å–±–∞, –¥–∑—é–¥–æ, –±–æ–∫—Å –∏ –ª–µ–≥–∫–∞—è –∞—Ç–ª–µ—Ç–∏–∫–∞. –≠—Ç–∏
            –∞—Ç–ª–µ—Ç—ã —Å—Ç–∞–Ω–æ–≤—è—Ç—Å—è —Å–∏–º–≤–æ–ª–∞–º–∏ —Å–∏–ª—ã, —É–ø–æ—Ä—Å—Ç–≤–∞ –∏ —Ä–µ—à–∏–º–æ—Å—Ç–∏. –ò—Ö
            –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è –≤–¥–æ—Ö–Ω–æ–≤–ª—è—é—Ç –Ω–æ–≤–æ–µ –ø–æ–∫–æ–ª–µ–Ω–∏–µ, –ø–æ–∫–∞–∑—ã–≤–∞—è, —á—Ç–æ —Å—Ç—Ä–µ–º–ª–µ–Ω–∏–µ –∫
            –ø–æ–±–µ–¥–µ –∏ —Ç—Ä—É–¥–æ–ª—é–±–∏–µ –æ—Ç–∫—Ä—ã–≤–∞—é—Ç –ø—É—Ç—å –∫ –≤–µ—Ä—à–∏–Ω–∞–º.
          </Typography>
        </Box>
      </Box>

      {/* –ë–ª–æ–∫ —Å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ–º */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="–ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
        />
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ —Å —Ñ–æ–Ω–æ–≤—ã–º –≤–∏–¥–µ–æ */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100vh",
            objectFit: "cover",
            zIndex: -1,
            filter: "brightness(50%)",
            objectFit: "cover",
            // height: "100vh",
          }}
        />
        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.5)", // –ü—Ä–æ–∑—Ä–∞—á–Ω—ã–π —Ñ–æ–Ω
            borderRadius: "10px", // –°–∫—Ä—É–≥–ª–µ–Ω–∏–µ —É–≥–ª–æ–≤
            padding: "20px",
            width: "100%",
            maxWidth: "700px",
            margin: "0 auto",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.2)", // –¢–µ–Ω—å –¥–ª—è –∫–∞—Ä—Ç–æ—á–∫–∏
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: "white", marginBottom: "20px" }}
          >
            {question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  transition: "all 0.3s ease",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "green"
                        : "red"
                      : "transparent", // –ü—Ä–æ–∑—Ä–∞—á–Ω—ã–π —Ñ–æ–Ω
                  color: "white",
                  border: "1px solid rgba(255, 255, 255, 0.3)", // –¢–æ–Ω–∫–∞—è –≥—Ä–∞–Ω–∏—Ü–∞
                  "&:hover": {
                    transform: "scale(1.05)",
                    backgroundColor: "#444",
                  },
                }}
              >
                <CardContent>
                  <Typography>{answer}</Typography>
                </CardContent>
              </Card>
            ))}
            {selectedAnswer && (
              <Typography
                sx={{
                  marginTop: "20px",
                  color: isCorrect ? "lightgreen" : "red",
                }}
              >
                {isCorrect ? "–í–µ—Ä–Ω–æ! üèîÔ∏è" : "–ù–µ–≤–µ—Ä–Ω–æ! –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑."}
              </Typography>
            )}
          </Box>
        </Card>
      </Box>
    </Box>
  );
};

export default MainPage;

