import React, { useState, useEffect } from "react";
import {
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Button,
  Typography,
  IconButton,
  Stack,
} from "@mui/material";
import {
  StarBorder,
  Star,
  FavoriteBorder,
  Favorite,
  // Иконка комментариев
} from "@mui/icons-material";
import MapsUgcIcon from "@mui/icons-material/MapsUgc";
import axios from "axios";
import { API_COMMENTS } from "../../helpers/const";

const Detail = ({ athlete, open, onClose }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [liked, setLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [showComments, setShowComments] = useState(false); // Состояние для видимости комментариев

  const handleLikeToggle = () => {
    setLiked((prevLiked) => !prevLiked);
    if (!liked) {
      setLikesCount((prevCount) => prevCount + 1);
    } else {
      setLikesCount((prevCount) => prevCount - 1);
    }
  };

  const handleFavoriteClick = () => {
    setIsFavorite((prevFavorite) => !prevFavorite);
    localStorage.setItem(`favorite_${athlete.id}`, (!isFavorite).toString());
  };

  const handleAddComment = async () => {
    if (newComment.trim() !== "") {
      const newCommentData = {
        id: Date.now(),
        text: newComment,
        productId: athlete.id,
      };
      const updatedComments = [...comments, newCommentData];
      setComments(updatedComments);
      setNewComment("");
      try {
        await axios.post(API_COMMENTS, newCommentData);
      } catch (error) {
        console.error("Ошибка при добавлении комментария на сервер:", error);
      }
    }
  };

  const handleDeleteComment = async (commentId) => {
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );
    setComments(updatedComments);

    try {
      await axios.delete(`${API_COMMENTS}/${commentId}`);
    } catch (error) {
      console.error("Ошибка при удалении комментария на сервере:", error);
    }
  };

  useEffect(() => {
    const fetchCommentsFromServer = async () => {
      try {
        const response = await axios.get(API_COMMENTS);
        const productComments = response.data.filter(
          (comment) => comment.productId === athlete.id
        );
        setComments(productComments);
      } catch (error) {
        console.error("Ошибка при загрузке комментариев с сервера:", error);
      }
    };
    fetchCommentsFromServer();
  }, [athlete.id]);

  return (
    <Dialog
      open={open}
      onClose={onClose}
      // fullWidth={false}
      // maxWidth="xs"
      sx={{
        ".MuiPaper-root": {
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "400px",
          height: "530px",
          boxShadow: 24,
          padding: 4,
          backgroundColor: "rgba(0, 0, 0, 0.9)", // Прозрачный фон для модального окна
        },
      }}
    >
      <DialogTitle
        style={{
          fontSize: "28px",
          fontWeight: "400",
          color: "#edf2fb",
          marginLeft: "35px",
        }}
      >
        {athlete.name}
      </DialogTitle>
      <DialogContent>
        <img
          src={athlete.image}
          alt={athlete.name}
          style={{ width: "100%", borderRadius: "8px", marginBottom: "16px" }}
        />
        <Typography
          variant="h6"
          style={{ fontSize: "20px", color: "#0466c8", marginLeft: "35px" }}
        >
          {athlete.sport}
        </Typography>
        <Typography
          variant="h6"
          style={{ fontSize: "23px", color: "red", marginLeft: "35px" }}
        >
          {athlete.country}
        </Typography>
        <Typography variant="body1">{athlete.details}</Typography>

        {/* Объединение иконок лайка и комментариев в одном ряду */}
        <Stack direction="row" spacing={2} alignItems="center" sx={{ mt: 2 }}>
          <IconButton
            onClick={handleLikeToggle}
            sx={{
              color: liked ? "#d90429" : "grey",
              "&:hover": {
                color: "#d90429", // Цвет при наведении на иконку
              },
            }}
          >
            {liked ? <Favorite /> : <FavoriteBorder />} {/* Иконка лайка */}
          </IconButton>
          <Typography variant="body2" style={{ color: "white" }}>
            ({likesCount} лайков)
          </Typography>

          {/* Иконка комментариев */}
          <IconButton
            onClick={() => setShowComments(!showComments)}
            sx={{
              color: "grey",
              "&:hover": {
                color: "#0466c8", // Цвет синий при наведении на иконку
              },
            }}
          >
            <MapsUgcIcon />
          </IconButton>
          <Typography variant="body2" style={{ color: "white" }}>
            {comments.length}{" "}
            {comments.length === 1 ? "комментарий" : "комментариев"}
          </Typography>
        </Stack>

        {showComments && (
          <>
            <Typography variant="h6" sx={{ mt: 2, color: "white" }}>
              Комментарии
            </Typography>
            {comments.length === 0 ? (
              <Typography variant="body2" color="#90caf9">
                Нет комментариев
              </Typography>
            ) : (
              comments.map((comment) => (
                <div key={comment.id}>
                  <Typography variant="body2" color="#cae9ff">
                    {comment.text}
                  </Typography>
                  <Button
                    onClick={() => handleDeleteComment(comment.id)}
                    variant="contained"
                    color="error"
                    sx={{
                      ml: 1,
                      flexGrow: 1,
                      "&:hover": {
                        backgroundColor: "#a4133c", // Увеличенная тень при наведении
                      },
                    }}
                  >
                    Удалить
                  </Button>
                </div>
              ))
            )}
            <TextField
              label="Добавить комментарий"
              variant="outlined"
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              fullWidth
              multiline
              rows={0.75}
              sx={{
                mt: 0.75,
                "& .MuiOutlinedInput-root": {
                  backgroundColor: "#0d47a1", // Синий фон для инпута
                  color: "white", // Белый текст в инпуте
                },
                "& .MuiInputLabel-root": {
                  color: "white", // Белый цвет для лейбла
                },
                "& .MuiOutlinedInput-notchedOutline": {
                  borderColor: "white", // Белая рамка вокруг инпута
                },
              }}
            />
            <Button
              onClick={handleAddComment}
              variant="contained"
              color="success"
              sx={{ mt: 2, mr: 2 }}
            >
              Добавить
            </Button>
          </>
        )}

        <Button
          onClick={onClose}
          variant="contained"
          color="secondary"
          sx={{ mt: 2, display: "block", margin: "40px auto" }}
        >
          Закрыть
        </Button>
      </DialogContent>
    </Dialog>
  );
};

export default Detail;
