import React, { useState } from "react";
import {
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Button,
  IconButton,
  Typography,
  Stack,
  styled,
  Grid, // Добавляем Grid для адаптивного размещения
} from "@mui/material";
import { useProduct } from "../context/ProductContextProvider";
import { useNavigate } from "react-router-dom";
import { StarBorder, Star } from "@mui/icons-material";
import Detail from "./Detail";

// Стилизованная кнопка, меняющая цвет при наведении
const AnimatedIconButton = styled(IconButton)({
  transition: "color 0.3s ease",
  "&:hover": {
    color: "#ff8c00",
  },
});

const ProductCard = ({ product }) => {
  const { deleteAthlete, getOneAthlete } = useProduct();
  const navigate = useNavigate();

  // Состояние для открытия деталей
  const [isDetailOpen, setDetailOpen] = useState(false);
  const [isFavorite, setIsFavorite] = useState(() => {
    const savedFavorite = localStorage.getItem(`favorite_${product.id}`);
    return savedFavorite === "true";
  });

  // Открытие и закрытие деталей
  const handleDetailOpen = () => setDetailOpen(true);
  const handleDetailClose = () => setDetailOpen(false);

  // Удаление продукта
  const handleRemoveProduct = () => {
    deleteAthlete(product.id);
  };

  // Редактирование продукта
  const handleEditProduct = () => {
    getOneAthlete(product.id);
    navigate(`/edit/${product.id}`); // Навигация к редактированию продукта
  };

  // Логика для изменения избранного
  const handleFavoriteClick = () => {
    setIsFavorite((prevFavorite) => {
      const newFavoriteState = !prevFavorite;
      localStorage.setItem(
        `favorite_${product.id}`,
        newFavoriteState.toString()
      );
      return newFavoriteState;
    });
  };

  return (
    <Grid item xs={12} sm={6} md={3} lg={3}>
      {" "}
      {/* Добавляем Grid */}
      <Card
        sx={{
          maxWidth: 320,
          width: "100%", // Устанавливаем 100% ширины карточки для адаптивности
          margin: "16px",
          display: "flex",
          flexDirection: "column",
          boxShadow: "0 8px 16px rgba(0, 0, 0, 0.2)",
          borderRadius: "35px",
          transition: "transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out",
          "&:hover": {
            transform: "scale(1.05)",
          },
        }}
      >
        <CardActionArea onClick={handleDetailOpen}>
          <CardMedia
            component="img"
            height="250"
            image={product.image || "https://via.placeholder.com/400"} // Используем изображение продукта или запасное изображение
            alt={product.name}
            sx={{
              objectFit: "cover",
              borderTopLeftRadius: 8,
              borderTopRightRadius: 8,
            }}
          />
          <CardContent>
            <Typography
              gutterBottom
              variant="h5"
              component="div"
              sx={{ textAlign: "center" }} // Выравнивание текста по центру
            >
              {product.name}
            </Typography>
            <Typography
              variant="h6"
              color="text.secondary"
              sx={{ textAlign: "center" }} // Выравнивание текста по центру
            >
              {product.sport} <br />
              {product.country} <br />
              {product.details}
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardContent>
          <Stack direction="row" spacing={1} sx={{ mt: 2 }}>
            {/* Кнопка редактирования */}
            <Button
              variant="contained"
              size="small"
              color="primary"
              sx={{
                flexGrow: 1,
                mt: "0px",
                "&:hover": {
                  backgroundColor: "#0d47a1", // Увеличенная тень при наведении
                },
              }}
              onClick={handleEditProduct}
            >
              Редактировать
            </Button>

            {/* Кнопка удаления */}
            <Button
              variant="contained"
              size="small"
              color="error"
              sx={{
                ml: 1,
                flexGrow: 1,
                "&:hover": {
                  backgroundColor: "#9b0000", // Увеличенная тень при наведении
                },
              }}
              onClick={handleRemoveProduct}
            >
              Удалить
            </Button>

            {/* Кнопка избранного */}
            <AnimatedIconButton onClick={handleFavoriteClick} sx={{ ml: 4 }}>
              {isFavorite ? <Star sx={{ color: "#ffb300" }} /> : <StarBorder />}
            </AnimatedIconButton>
          </Stack>
        </CardContent>

        {/* Компонент для отображения подробной информации о продукте */}
        {isDetailOpen && (
          <Detail
            athlete={product}
            open={isDetailOpen}
            onClose={handleDetailClose}
          />
        )}
      </Card>
    </Grid>
  );
};

export default ProductCard;

