import React, { createContext, useContext, useState, useEffect } from "react";
import fire from "../../fire";
import { useNavigate } from "react-router-dom";

// Создаем контекст авторизации
const authContext = createContext();

// Кастомный хук для использования контекста авторизации
export const useAuth = () => useContext(authContext);

// Компонент провайдера контекста авторизации
const AuthContextProvider = ({ children }) => {
  // Состояния для управления формой и пользователем
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [formError, setFormError] = useState(""); // Новое состояние для ошибок формы
  const navigate = useNavigate(); // Хук для навигации

  // Функция для регистрации пользователя
  const handleRegister = async () => {
    try {
      await fire.auth().createUserWithEmailAndPassword(email, password);
      setEmail(""); // Сбрасываем значение email
      setPassword(""); // Сбрасываем значение password
      navigate("/"); // Перенаправляем пользователя после успешной регистрации
    } catch (error) {
      // Обработка ошибок регистрации
      switch (error.code) {
        case "auth/email-already-in-use":
          setEmailError(
            "Этот email уже используется. Пожалуйста, выберите другой."
          );
          break;
        case "auth/invalid-email":
          setEmailError("Неверный формат email.");
          break;
        case "auth/weak-password":
          setPasswordError("Пароль слишком слабый.");
          break;
        default:
          setFormError("Произошла ошибка при регистрации. Попробуйте снова.");
          break;
      }
    }
  };

  // Функция для входа пользователя
  const handleLogin = async () => {
    try {
      await fire.auth().signInWithEmailAndPassword(email, password);
      setEmail(""); // Сбрасываем значение email
      setPassword(""); // Сбрасываем значение password
      navigate("/"); // Перенаправляем пользователя после успешного входа
    } catch (error) {
      // Обработка ошибок входа
      switch (error.code) {
        case "auth/user-disabled":
          setFormError("Аккаунт заблокирован.");
          break;
        case "auth/invalid-email":
          setEmailError("Неверный формат email.");
          break;
        case "auth/user-not-found":
          setFormError("Пользователь с таким email не найден.");
          break;
        case "auth/wrong-password":
          setPasswordError("Неверный пароль.");
          break;
        default:
          setFormError("Произошла ошибка при входе. Попробуйте снова.");
          break;
      }
    }
  };

  // Функция для выхода пользователя
  const handleLogOut = async () => {
    await fire.auth().signOut();
    navigate("/"); // Перенаправляем пользователя после выхода
  };

  // Функция для отслеживания состояния авторизации пользователя
  const authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        setUser(user); // Устанавливаем пользователя в состояние, если он авторизован
      } else {
        setUser(null); // Сбрасываем состояние пользователя, если он не авторизован
      }
      setLoading(false); // Устанавливаем флаг, что загрузка завершена
    });
  };

  // Используем хук useEffect для вызова authListener при монтировании компонента
  useEffect(() => {
    authListener();
  }, []);

  // Возвращаем провайдер контекста с передаваемыми значениями и оборачиваем в него дочерние компоненты
  return (
    <authContext.Provider
      value={{
        email,
        setEmail,
        password,
        setPassword,
        user,
        loading,
        emailError,
        passwordError,
        formError, // Добавлено новое состояние ошибки формы
        handleRegister,
        handleLogin,
        handleLogOut,
      }}
    >
      {children}
    </authContext.Provider>
  );
};

export default AuthContextProvider;
