import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const questions = [
  {
    question:
      "Сколько золотых медалей завоевали спортсмены Кыргызстана на Олимпийских играх?",
    options: ["0", "1", "3", "5"],
    correct: 1,
  },
  {
    question:
      "Какой вид спорта наиболее успешен для Кыргызстана на Олимпиадах?",
    options: ["Борьба", "Бокс", "Легкая атлетика", "Дзюдо"],
    correct: 0,
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizResults, setQuizResults] = useState(0);
  const [quizFeedback, setQuizFeedback] = useState("");

  useEffect(() => {
    const handleScroll = () => {
      setIsVisible(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (idx) => {
    setSelectedOption(idx);
    if (idx === questions[currentQuestion].correct) {
      setQuizFeedback("Правильный ответ!");
      setQuizResults((prev) => prev + 1);
    } else {
      setQuizFeedback("Неправильный ответ!");
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption(null);
      setQuizFeedback("");
    } else {
      setShowQuiz(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{ fontSize: "48px", fontWeight: "bold" }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{ fontSize: "20px", lineHeight: "1.6" }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{ display: "flex", justifyContent: "center", marginTop: "30px" }}
      >
        <Box
          sx={{
            position: "relative",
            padding: "40px 20px",
            color: "white",
            textAlign: "center",
            marginTop: "30px",
            borderRadius: "10px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            backgroundColor: "#222",
            width: "90%",
            maxWidth: "600px",
          }}
        >
          <Typography
            variant="h2"
            sx={{ marginBottom: "20px", fontWeight: "bold", color: "#ffcc00" }}
          >
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>

          {showQuiz ? (
            <Typography
              sx={{
                marginTop: "20px",
                fontSize: "24px",
                fontWeight: "bold",
                color: "green",
              }}
            >
              Результат: {quizResults} из {questions.length} правильных ответов
            </Typography>
          ) : (
            <Card
              sx={{
                maxWidth: 400,
                margin: "20px auto",
                padding: "20px",
                backgroundColor: "#333",
              }}
            >
              <CardContent>
                <Typography
                  variant="h5"
                  sx={{ fontSize: "22px", fontWeight: "600" }}
                >
                  {questions[currentQuestion].question}
                </Typography>
                <Box
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "10px",
                    marginTop: "20px",
                  }}
                >
                  {questions[currentQuestion].options.map((option, idx) => (
                    <Button
                      key={idx}
                      variant="contained"
                      color={selectedOption === idx ? "success" : "primary"}
                      onClick={() => handleOptionClick(idx)}
                      sx={{
                        transition: "all 0.3s ease",
                        ":hover": {
                          transform: "scale(1.1)",
                          background: "#ff4d6d",
                        },
                      }}
                    >
                      {option}
                    </Button>
                  ))}
                </Box>
                <Typography
                  sx={{
                    marginTop: "10px",
                    fontWeight: "bold",
                    color:
                      quizFeedback === "Правильный ответ!" ? "green" : "red",
                  }}
                >
                  {quizFeedback}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleNextQuestion}
                  sx={{ width: "100%", padding: "15px" }}
                >
                  {currentQuestion === questions.length - 1
                    ? "Завершить"
                    : "Следующий вопрос"}
                </Button>
              </CardActions>
            </Card>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

{
