import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormControl,
  FormLabel,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [score, setScore] = useState(0);

  const questions = [
    {
      question: "Какой вид спорта является национальным в Кыргызстане?",
      answers: ["Бокс", "Футбол", "Куреш", "Баскетбол"],
      correctAnswer: "Куреш",
    },
    {
      question: "В каком году Кыргызстан стал независимым?",
      answers: ["1991", "1990", "1992", "1989"],
      correctAnswer: "1991",
    },
    {
      question: "Какая гора является самой высокой в Кыргызстане?",
      answers: ["Пик Победы", "Пик Ленина", "Тянь-Шань", "Пик Октябрь"],
      correctAnswer: "Пик Победы",
    },
  ];

  const handleScroll = () => {
    if (window.scrollY > 200) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerChange = (event) => {
    setSelectedAnswer(event.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    setSelectedAnswer("");
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert(
        `Викторина завершена! Ваш результат: ${score} из ${questions.length}`
      );
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          marginTop: "40px",
          padding: "20px",
          backgroundColor: "#1a1a1a",
          borderRadius: "12px",
          maxWidth: "800px",
          margin: "0 auto",
          boxShadow: "0 4px 20px rgba(0, 0, 0, 0.3)",
        }}
      >
        <Typography variant="h4" sx={{ color: "white", marginBottom: "20px" }}>
          Викторина о Кыргызстане
        </Typography>
        <Card sx={{ marginBottom: "20px", backgroundColor: "#222" }}>
          <CardContent>
            <Typography
              variant="h6"
              sx={{ color: "white", marginBottom: "10px" }}
            >
              {questions[currentQuestion].question}
            </Typography>
            <FormControl component="fieldset">
              <RadioGroup
                value={selectedAnswer}
                onChange={handleAnswerChange}
                sx={{ display: "flex", flexDirection: "column" }}
              >
                {questions[currentQuestion].answers.map((answer, index) => (
                  <FormControlLabel
                    key={index}
                    value={answer}
                    control={<Radio sx={{ color: "#ffb400" }} />}
                    label={answer}
                    sx={{
                      color: "white",
                      marginBottom: "10px",
                      "&:hover": {
                        backgroundColor: "#333",
                      },
                    }}
                  />
                ))}
              </RadioGroup>
            </FormControl>
            <Button
              variant="contained"
              sx={{
                backgroundColor: "#ffb400",
                color: "#fff",
                padding: "10px 20px",
                marginTop: "20px",
                "&:hover": {
                  backgroundColor: "#e68900",
                },
              }}
              onClick={handleNextQuestion}
            >
              Следующий вопрос
            </Button>
          </CardContent>
        </Card>
        <Typography variant="body1" sx={{ color: "#ffb400" }}>
          Ваш текущий счёт: {score} из {questions.length}
        </Typography>
      </Box>
    </Box>
  );
};

export default MainPage;

