import React, { useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useAuth } from "../../components/context/AuthContextProvider";
import { ADMIN_EMAIL } from "../../helpers/const";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
import "./Navbar.css";

const Navbar = () => {
  const { user, handleLogOut } = useAuth();
  const [drawerOpen, setDrawerOpen] = useState(false);

  // Определяем, является ли пользователь администратором
  const isAdmin = user && user.email === ADMIN_EMAIL;

  // Список страниц
  const pages = [
    { id: 1, title: "Атлеты", link: "/athletes" },
    { id: 2, title: "Виды спорта", link: "/sports" },
    { id: 3, title: "Медальный зачет", link: "/medal-table" },
  ];

  if (isAdmin) {
    pages.push({ id: 4, title: "Админ", link: "/admin" });
  }

  const toggleDrawer = (open) => {
    setDrawerOpen(open);
  };

  return (
    <div style={styles.root}>
      <AppBar position="static" style={styles.appBar}>
        <Toolbar style={styles.toolbar}>
          <div style={styles.leftItems}>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Flag_of_Kyrgyzstan_%283-2%29.svg/1599px-Flag_of_Kyrgyzstan_%283-2%29.svg.png?20240311163127"
              style={{ ...styles.icon, width: "auto", height: 40 }}
              alt="Flag"
            />
            <Link to={"/"} style={{ textDecoration: "none" }}>
              <Typography variant="h6" style={styles.menuItem}>
                Спортсмены КР
              </Typography>
            </Link>
          </div>

          <div style={{ ...styles.centerItems, flex: 1 }}>
            {pages.map((page, index) => (
              <IconButton
                key={page.id}
                color="inherit"
                component={Link}
                to={page.link}
                style={{
                  ...styles.menuButton,
                  marginLeft: index > 0 ? 10 : 0,
                }}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  {page.title}
                </Typography>
              </IconButton>
            ))}
          </div>

          <div style={styles.rightItems}>
            <Typography
              variant="body1"
              style={{ color: "#ffffff", marginRight: "15px" }}
            >
              {user
                ? `Добро пожаловать, ${user.displayName || user.email}`
                : "Добро пожаловать, гость"}
            </Typography>
            <IconButton
              edge="end"
              color="inherit"
              aria-label="menu"
              onClick={() => toggleDrawer(true)}
              style={styles.menuButton}
            >
              <MenuIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => toggleDrawer(false)}
        style={styles.drawer}
        transitionDuration={500}
      >
        <div style={styles.drawerHeader}>
          <IconButton
            onClick={() => toggleDrawer(false)}
            style={styles.closeButton}
          >
            <CloseIcon />
          </IconButton>
        </div>
        <List style={styles.drawerList}>
          <ListItem>
            <Typography style={{ color: "#000000" }}>
              {user
                ? isAdmin
                  ? `Добро пожаловать, ${user.email}`
                  : `Добро пожаловать, ${user.displayName || user.email}`
                : "Добро пожаловать, гость"}
            </Typography>
          </ListItem>
          {!user && (
            <ListItem button component={Link} to="/register">
              <ListItemText primary="Регистрация" />
            </ListItem>
          )}
          {user && (
            <ListItem button onClick={handleLogOut}>
              <ListItemText primary="Выйти" />
            </ListItem>
          )}
          {pages.map((page) => (
            <ListItem button key={page.id} component={Link} to={page.link}>
              <ListItemText primary={page.title} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </div>
  );
};

const styles = {
  root: {
    flexGrow: 1,
    width: "100%",
    height: "auto",
    overflow: "hidden",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)", // Shadow for the AppBar
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flex: 1,
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
  },
  menuButton: {
    marginRight: 10,
  },
  menuItem: {
    color: "#ffffff",
    fontSize: "1rem", // Slightly bigger font for better visibility
    fontWeight: "bold", // Uniform text weight for consistency
  },
  icon: {
    width: "auto",
    height: 40,
    marginRight: 10,
  },
  drawerList: {
    width: 250,
  },
  drawerHeader: {
    display: "flex",
    justifyContent: "flex-end",
    padding: "10px",
  },
  closeButton: {
    color: "#000",
  },
  drawer: {
    backgroundColor: "rgba(0, 0, 0, 0.9)", // Darker background for a modern look
    transition: "background-color 0.3s ease",
    paddingTop: "10px", // Add padding to top for better alignment
  },
};

export default Navbar;
