import {
  Avatar,
  Box,
  Button,
  Container,
  Grid,
  IconButton,
  InputAdornment,
  OutlinedInput,
  TextField,
  Typography,
} from "@mui/material";
import React, { useState, useEffect } from "react";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { useAuth } from "../components/context/AuthContextProvider";

const Auth = () => {
  const [showPassword, setShowPassword] = useState(false);
  const handleClickShowPassword = () => setShowPassword((show) => !show);
  const handleMouseDownPassword = (event) => event.preventDefault();
  const handleSubmit = (event) => event.preventDefault();

  const {
    email,
    password,
    emailError,
    passwordError,
    setEmail,
    setPassword,
    user,
    setUser,
    handleRegister,
    handleLogin,
    handleLogOut,
    authListener,
    hasAccount,
    setHasAccount,
  } = useAuth();

  // Локальное состояние для имени пользователя
  const [userName, setUserName] = useState("");

  // Используем эффект для получения имени пользователя после успешной регистрации
  useEffect(() => {
    if (user) {
      // Если пользователь авторизован, отображаем его имя
      setUserName(user.displayName || "User"); // Можно заменить на свое поле, если displayName пустое
    }
  }, [user]);

  return (
    <Container component="main" maxWidth="xs">
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          {hasAccount ? "Login Form" : "Register Now"}
        </Typography>

        {/* Отображаем имя пользователя, если он зарегистрирован */}
        {userName && <Typography variant="h6">Hello, {userName}!</Typography>}

        <Box onSubmit={handleSubmit} noValidate component="form" sx={{ mt: 1 }}>
          <TextField
            label="Email Address"
            name="email"
            autoFocus
            autoComplete="email"
            required
            fullWidth
            margin="normal"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            helperText={emailError}
          />

          <OutlinedInput
            autoComplete="current-password"
            id="outlined-adornment-password"
            fullWidth
            required
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            helperText={passwordError}
            type={showPassword ? "text" : "password"}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
          {hasAccount ? (
            <Button
              fullWidth
              type="submit"
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              onClick={handleLogin}
            >
              Login
            </Button>
          ) : (
            <Button
              fullWidth
              type="submit"
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              onClick={handleRegister}
            >
              Register Now
            </Button>
          )}
          <Grid container>
            <Grid item>
              <Typography
                variant="body2"
                onClick={() => setHasAccount(!hasAccount)}
                sx={{ cursor: "pointer", textDecoration: "underline" }}
              >
                {hasAccount
                  ? "Don`t have an account? Regsiter Now"
                  : "Already have an account? Login "}
              </Typography>
            </Grid>
          </Grid>
        </Box>
      </Box>
    </Container>
  );
};

export default Auth;
