import React from "react";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  return (
    <div
      className="home-main-page"
      style={{ position: "relative", minHeight: "100vh", overflow: "hidden" }}
    >
      {/* Видео фон */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Затемнение видео без сплошного черного фона
          }}
        />
        {/* Градиентный оверлей */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.4), rgba(0,0,0,0.8))",
          }}
        ></div>
      </div>

      {/* Контент */}
      <div
        style={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
        }}
      >
        <div style={{ maxWidth: "800px" }}>
          <h1
            style={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              textTransform: "uppercase",
              letterSpacing: "2px",
            }}
          >
            Спортсмены Кыргызстана
          </h1>
          <p
            style={{
              fontSize: "clamp(16px, 2vw, 22px)", // Адаптивный текст
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Кыргызские спортсмены достойно представляют свою страну на мировой
            арене, демонстрируя силу, выносливость и волю к победе. Вольная и
            греко-римская борьба, дзюдо, тяжелая атлетика, бокс и легкая
            атлетика — главные дисциплины, в которых Кыргызстан завоевывает
            медали. Среди известных спортсменов — Айсулуу Тыныбекова, первая
            чемпионка мира по борьбе, Акжол Махмудов, олимпийский призер, и
            Уланбек Карыбек уулу, выдающийся легкоатлет. Их достижения
            вдохновляют новое поколение кыргызстанцев, доказывая, что упорство и
            трудолюбие ведут к успеху.
          </p>
        </div>
      </div>

      {/* Блок с изображением */}
      <div
        style={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease",
          }}
          onMouseOver={(e) => (e.target.style.transform = "scale(1.03)")}
          onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
        />
      </div>
    </div>
  );
};

export default MainPage;

