import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import Confetti from "react-confetti"; // Подключаем библиотеку для салютов
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import natureKR from "../homePage/assets/natureKR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false); // Для отображения салютов
  const [shake, setShake] = useState(false); // Для анимации неверного ответа

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const question = "Какой самый высокий пик в Кыргызстане?";
  const answers = ["Пик Ленина", "Пик Победы", "Пик Манас", "Пик Корона"];
  const correctAnswer = "Пик Победы";

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === correctAnswer);
    if (answer === correctAnswer) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000); // Салют будет отображаться 5 секунд
    } else {
      setShake(true); // Запуск анимации для неверного ответа
      setTimeout(() => setShake(false), 500); // Убираем анимацию через 500 мс
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
        />
      </Box>

      {/* Викторина с фоновым видео */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover", // Измени objectFit
            filter: "none",
            zIndex: -1,
          }}
        />

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)", // Затемнённый полупрозрачный фон
            borderRadius: "10px", // Скругление углов
            padding: "20px",
            width: "100%",
            maxWidth: "600px",
            margin: "0 auto",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.2)", // Тень для карточки
            backdropFilter: "blur(10px)", // Эффект размытия для лучшей интеграции с видеофоном
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: "white", marginBottom: "20px" }}
          >
            {question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  transition: "all 0.3s ease",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "rgba(0, 150, 0, 0.7)" // Полупрозрачный зелёный для верного ответа
                        : "rgba(150, 0, 0, 0.7)" // Полупрозрачный красный для неверного ответа
                      : "rgba(255, 255, 255, 0.1)", // Лёгкий прозрачный цвет для карточек
                  color: "white",
                  border: "1px solid rgba(255, 255, 255, 0.3)",
                  "&:hover": {
                    transform: "scale(1.05)",
                    backgroundColor: "rgba(255, 255, 255, 0.3)", // Подсветка при наведении
                    filter: "brightness(1.2)", // Яркость для подсветки
                  },
                }}
              >
                <CardContent>
                  <Typography>{answer}</Typography>
                </CardContent>
              </Card>
            ))}
            {selectedAnswer && (
              <Typography
                sx={{
                  marginTop: "20px",
                  color: isCorrect ? "lightgreen" : "red",
                  animation: shake ? "shake 0.5s" : "none", // Применение анимации при неверном ответе
                }}
              >
                {isCorrect ? "Верно! 🏔️" : "Неверно! Попробуйте ещё раз."}
              </Typography>
            )}
          </Box>
        </Card>
        {showConfetti && <Confetti />}
      </Box>
    </Box>
  );
};

export default MainPage;

