import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  useMediaQuery,
  useTheme,
  Divider,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useAuth } from "../../components/context/AuthContextProvider";
import { ADMIN_EMAIL } from "../../helpers/const";
import MenuIcon from "@mui/icons-material/Menu";
import SportsIcon from "@mui/icons-material/Sports";
import MedalIcon from "@mui/icons-material/EmojiEvents";
import PersonIcon from "@mui/icons-material/Person";
import AdminPanelSettingsIcon from "@mui/icons-material/AdminPanelSettings";
import ExitToAppIcon from "@mui/icons-material/ExitToApp";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const Navbar = () => {
  const { user, handleLogOut, loading } = useAuth();
  const [isAdmin, setIsAdmin] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    if (user) {
      setIsAdmin(user.email === ADMIN_EMAIL);
    }
  }, [user]);

  const pages = [
    { id: 1, title: "Атлеты", link: "/athletes", icon: <PersonIcon /> },
    { id: 2, title: "Виды спорта", link: "/sports", icon: <SportsIcon /> },
    {
      id: 3,
      title: "Медальный зачет",
      link: "/medal-table",
      icon: <MedalIcon />,
    },
  ];

  if (isAdmin) {
    pages.push({
      id: 4,
      title: "Админ",
      link: "/admin",
      icon: <AdminPanelSettingsIcon />,
    });
  }

  const toggleDrawer = (open) => {
    setDrawerOpen(open);
  };

  return (
    <div style={styles.root}>
      <AppBar position="static" style={styles.appBar}>
        <Toolbar style={styles.toolbar}>
          <Typography variant="h6" style={styles.logo}>
            Спортсмены КР
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={() => toggleDrawer(true)}
            style={styles.menuButton}
          >
            <MenuIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => toggleDrawer(false)}
        PaperProps={{ style: styles.drawerPaper }}
      >
        <List style={styles.drawerList}>
          {!loading && (
            <ListItem>
              <Typography style={{ color: "#000000" }}>
                {user
                  ? isAdmin
                    ? `Админ: ${user.email}`
                    : `Пользователь: ${user.displayName || user.email}`
                  : "Гость"}
              </Typography>
            </ListItem>
          )}
          <Divider />
          {pages.map((page) => (
            <ListItem button key={page.id} component={Link} to={page.link}>
              <ListItemIcon>{page.icon}</ListItemIcon>
              <ListItemText primary={page.title} />
            </ListItem>
          ))}
          <Divider />
          {!loading && !user && (
            <ListItem button component={Link} to="/register">
              <ListItemIcon>
                <PersonAddIcon />
              </ListItemIcon>
              <ListItemText primary="Регистрация" />
            </ListItem>
          )}
          {!loading && user && (
            <ListItem button onClick={handleLogOut}>
              <ListItemIcon>
                <ExitToAppIcon />
              </ListItemIcon>
              <ListItemText primary="Выйти" />
            </ListItem>
          )}
        </List>
      </Drawer>
    </div>
  );
};

const styles = {
  root: {
    flexGrow: 1,
    width: "100%",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  logo: {
    color: "#ffffff",
    textDecoration: "none",
    fontWeight: "bold",
  },
  menuButton: {
    color: "#ffffff",
  },
  drawerPaper: {
    width: 270,
    background: "linear-gradient(135deg, #f5f7fa, #c3cfe2)",
    boxShadow: "3px 0px 10px rgba(0, 0, 0, 0.2)",
  },
  drawerList: {
    paddingTop: 10,
  },
};

export default Navbar;
