import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // –ü—É—Ç—å –∫ –≤–∏–¥–µ–æ

const MainPage = () => {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [showNextButton, setShowNextButton] = useState(false);

  const questions = [
    {
      question:
        "–ö—Ç–æ –∏–∑ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–æ–≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ —Å—Ç–∞–ª –æ–ª–∏–º–ø–∏–π—Å–∫–∏–º —á–µ–º–ø–∏–æ–Ω–æ–º –≤ –¢–æ–∫–∏–æ?",
      options: [
        "–ì—É–ª—å–Ω–∞—Ä–∞ –ë–µ–∫–º–∞–º–∞—Ç–æ–≤–∞",
        "–ú–µ–¥–µ—Ç –°–∞–π–¥—É–ª–∞–µ–≤",
        "–£–ª–∞–Ω–±–µ–∫ –ê–º–∏—Ä–æ–≤",
        "–°–∞–∏–¥–∂–æ–Ω –ú–∞–¥–∞–ª–∏–µ–≤",
      ],
      correct: 0,
    },
    {
      question:
        "–ö–∞–∫–æ–π –≤–∏–¥ —Å–ø–æ—Ä—Ç–∞ –ø—Ä–∏–Ω–µ—Å –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω—É –ø–µ—Ä–≤—É—é –æ–ª–∏–º–ø–∏–π—Å–∫—É—é –º–µ–¥–∞–ª—å?",
      options: ["–ë–æ–∫—Å", "–ë–æ—Ä—å–±–∞", "–î–∑—é–¥–æ", "–õ–µ–≥–∫–∞—è –∞—Ç–ª–µ—Ç–∏–∫–∞"],
      correct: 1,
    },
    {
      question:
        "–°–∫–æ–ª—å–∫–æ –º–µ–¥–∞–ª–µ–π –∑–∞–≤–æ–µ–≤–∞–ª–∏ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ –Ω–∞ –û–ª–∏–º–ø–∏–∞–¥–µ 2016 –≥–æ–¥–∞?",
      options: ["1 –º–µ–¥–∞–ª—å", "2 –º–µ–¥–∞–ª–∏", "3 –º–µ–¥–∞–ª–∏", "4 –º–µ–¥–∞–ª–∏"],
      correct: 1,
    },
  ];

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[questionIndex].correct;
    if (index === correctOption) {
      setQuizFeedback("–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ");
    } else {
      setQuizFeedback("–ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.");
    }
    setShowNextButton(true);
  };

  const handleNextQuestion = () => {
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
      setSelectedOption(null);
      setQuizFeedback("");
      setShowNextButton(false);
    } else {
      setQuizFeedback("–ü–æ–∑–¥—Ä–∞–≤–ª—è–µ–º! –í—ã –∑–∞–≤–µ—Ä—à–∏–ª–∏ –≤–∏–∫—Ç–æ—Ä–∏–Ω—É.");
      setShowNextButton(false);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // –°–¥–µ–ª–∞–ª —Ñ–æ–Ω —Ç–µ–º–Ω–µ–µ –¥–ª—è –ª—É—á—à–µ–≥–æ –∫–æ–Ω—Ç—Ä–∞—Å—Ç–∞
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // –ë–æ–ª–µ–µ —Ç–µ–º–Ω—ã–π –æ–≤–µ—Ä–ª–µ–π
          }}
        ></div>
      </Box>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç —Å –∞–Ω–∏–º–∞—Ü–∏–µ–π */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            –í–∏–∫—Ç–æ—Ä–∏–Ω–∞: –û–ª–∏–º–ø–∏–π—Å–∫–∏–µ —á–µ–º–ø–∏–æ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            –ü—Ä–æ–π–¥–∏—Ç–µ –≤–∏–∫—Ç–æ—Ä–∏–Ω—É, —á—Ç–æ–±—ã –ø—Ä–æ–≤–µ—Ä–∏—Ç—å —Å–≤–æ–∏ –∑–Ω–∞–Ω–∏—è –æ–± –æ–ª–∏–º–ø–∏–π—Å–∫–∏—Ö
            —á–µ–º–ø–∏–æ–Ω–∞—Ö –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞!
          </Typography>
        </Box>
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <Typography
          variant="h5"
          sx={{
            marginBottom: "15px",
            fontSize: "22px",
            fontWeight: "600",
            textAlign: "center",
            color: "#fff",
            animation: "fadeInUp 0.5s ease-out",
          }}
        >
          {questions[questionIndex].question}
        </Typography>
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: "15px",
          }}
        >
          {questions[questionIndex].options.map((option, idx) => (
            <Button
              key={idx}
              variant="contained"
              color={selectedOption === idx ? "success" : "primary"}
              onClick={() => handleOptionClick(idx)}
              sx={{
                background:
                  selectedOption === idx
                    ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                    : "#007bff",
                transition: "all 0.3s ease",
                ":hover": {
                  transform: "scale(1.1)",
                  background: "#ff4d6d",
                },
                padding: "15px 30px",
                borderRadius: "10px",
                fontSize: "16px",
                boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              }}
            >
              {option}
            </Button>
          ))}
        </Box>
        <Typography
          sx={{
            marginTop: "20px",
            fontWeight: "bold",
            fontSize: "18px",
            color: quizFeedback === "–ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! üéâ" ? "green" : "red",
            animation: "fadeIn 0.5s ease-out",
          }}
        >
          {quizFeedback}
        </Typography>
        {showNextButton && (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleNextQuestion}
            sx={{
              marginTop: "30px",
              padding: "12px 20px",
              fontSize: "18px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease",
              ":hover": { transform: "scale(1.05)" },
            }}
          >
            –°–ª–µ–¥—É—é—â–∏–π –≤–æ–ø—Ä–æ—Å
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

