import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Typography, Button, Container, Box } from "@mui/material";

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    description: "Впервые в своей карьере!!!",
    date: "21:59, 30.03.2025",
    image:
      "https://prosports.kg/upload/news/main//202503/254179_51d2d45eff3b9e8645a242d95de7a921.webp",
    content:
      "Эрназар Акматалиев завоевал первое место на чемпионате Азии по борьбе в 2025 году. Этот успех стал результатом упорных тренировок и настойчивости.\n\nКыргызстанский спортсмен Эрназар Акматалиев стал победителем чемпионата Азии по борьбе в Иордании, сообщает Prosports.kz.",
  },
];

const NewsDetailsPages = () => {
  const { id } = useParams();
  const newsItem = newsData.find((news) => news.id.toString() === id);
  const navigate = useNavigate();

  if (!newsItem) {
    return <Typography variant="h6">Новость не найдена</Typography>;
  }

  return (
    <Box
      sx={{
        backgroundColor: "#1a1a1a", // Чёрный фон на всю страницу
        minHeight: "100vh", // Высота на весь экран
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Container
        maxWidth="md"
        sx={{
          py: 4,
          backgroundColor: "#1a1a1a", // Тёмно-серый фон контейнера
          color: "#fff",
          borderRadius: "8px",
          boxShadow: "0px 4px 10px rgba(255, 255, 255, 0.2)",
        }}
      >
        <Button
          onClick={() => navigate("/news")}
          sx={{
            mb: 3,
            backgroundColor: "#ff4d6d",
            color: "#fff",
            ":hover": { backgroundColor: "#ff1a4d" },
          }}
        >
          Назад к новостям
        </Button>

        <Typography variant="h4" fontWeight="bold" gutterBottom>
          {newsItem.title}
        </Typography>
        <Typography variant="body2" color="text.secondary" mb={2}>
          {newsItem.date}
        </Typography>

        <Box
          component="img"
          src={newsItem.image}
          alt={newsItem.title}
          sx={{
            width: "80%",
            height: "auto",
            borderRadius: "10px",
            boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)",
            mb: 2,
          }}
        />
        <Typography variant="body1" lineHeight={1.8}>
          {newsItem.content}
        </Typography>
      </Container>
    </Box>
  );
};

export default NewsDetailsPages;
